// src/utils/api.js
// Centralized Axios client + API helpers with smart base URL handling

import axios from 'axios';

/* =========================
   Resolve Base URL safely
   - Accepts Vite (VITE_API_BASE_URL | VITE_API_URL)
   - Accepts CRA  (REACT_APP_API_BASE_URL | REACT_APP_API_URL)
   - Works whether the env already includes /api or not
   ========================= */
function pickEnvBase() {
  const vite =
    (typeof import.meta !== 'undefined' && (import.meta.env?.VITE_API_BASE_URL || import.meta.env?.VITE_API_URL)) ||
    null;

  const cra =
    (typeof process !== 'undefined' &&
      (process.env.REACT_APP_API_BASE_URL || process.env.REACT_APP_API_URL)) ||
    null;

  return vite || cra || 'http://localhost:5000';
}

// Normalize, strip duplicate /api
function buildRoots(raw) {
  let base = String(raw || '').trim();
  // remove trailing slash
  base = base.replace(/\/+$/, '');
  // if env already ends with /api, remove it; we'll append one cleanly below
  base = base.replace(/\/api$/i, '');
  const API_ROOT = `${base}/api`;
  return { API_BASE_RESOLVED: base, API_ROOT };
}

const { API_BASE_RESOLVED, API_ROOT } = buildRoots(pickEnvBase());
export { API_BASE_RESOLVED as API_BASE, API_ROOT };

/* =========================
   Axios instance & token
   ========================= */
export const http = axios.create({
  baseURL: API_ROOT,
  withCredentials: false, // using Bearer token header
  headers: { 'Content-Type': 'application/json' },
});

export function setAuthToken(token) {
  if (token) {
    http.defaults.headers.common.Authorization = `Bearer ${token}`;
  } else {
    delete http.defaults.headers.common.Authorization;
  }
}

/* =========================
   Interceptors
   ========================= */
http.interceptors.response.use(
  (res) => res,
  (error) => {
    const status = error?.response?.status;
    const data = error?.response?.data;
    const message = data?.message || data?.error || error?.message || `HTTP ${status || 'ERR'}`;
    error.normalizedMessage = message;
    return Promise.reject(error);
  }
);

/* =========================
   Unwrap helper
   ========================= */
const unwrap = (p) =>
  p.then((r) => r.data).catch((e) => {
    throw new Error(e.normalizedMessage || e.message || 'Request failed');
  });

/* =========================
   Health
   ========================= */
export const healthAPI = {
  ping: () => unwrap(http.get('/health')),
};

/* =========================
   Auth
   ========================= */
export const authAPI = {
  login: (credentials) => unwrap(http.post('/auth/login', credentials)),
  me: () => unwrap(http.get('/auth/me')),
  logout: () => unwrap(http.post('/auth/logout')),
};

/* =========================
   Jobs
   ========================= */
export const jobsAPI = {
  list: (params = {}) => unwrap(http.get('/jobs', { params })),
  get: (id) => unwrap(http.get(`/jobs/${id}`)),
  create: (payload) => unwrap(http.post('/jobs', payload)),
  update: (id, payload) => unwrap(http.put(`/jobs/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/jobs/${id}`)),
};
// Compat aliases for legacy code
jobsAPI.getAll = jobsAPI.list;

/* =========================
   Companies
   ========================= */
export const companiesAPI = {
  list: (params = {}) => unwrap(http.get('/companies', { params })),
  get: (id) => unwrap(http.get(`/companies/${id}`)),
  create: (payload) => unwrap(http.post('/companies', payload)),
  update: (id, payload) => unwrap(http.put(`/companies/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/companies/${id}`)),
};
// Compat
companiesAPI.getAll = companiesAPI.list;

/* =========================
   Profile (FIXED)
   ========================= */
export const profileAPI = {
  getMe: () => unwrap(http.get('/profile/me')),
  updateMe: (data) => unwrap(http.put('/profile/me', data)),
};

/* =========================
   Users (Admin)
   ========================= */
export const usersAPI = {
  list: (params = {}) => unwrap(http.get('/users', { params })), // ?role=&status=&course=
  get: (id) => unwrap(http.get(`/users/${id}`)),
  create: (payload) => unwrap(http.post('/users', payload)),
  update: (id, payload) => unwrap(http.put(`/users/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/users/${id}`)),
};
// Compat
usersAPI.getAll = usersAPI.list;

/* =========================
   Surveys
   ========================= */
export const surveysAPI = {
  list: (params = {}) => unwrap(http.get('/surveys', { params })),
  get: (id) => unwrap(http.get(`/surveys/${id}`)),
  create: (payload) => unwrap(http.post('/surveys', payload)),
  update: (id, payload) => unwrap(http.put(`/surveys/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/surveys/${id}`)),
  listResponses: (surveyId, params = {}) =>
    unwrap(http.get(`/surveys/${surveyId}/responses`, { params })),
};
// Compat
surveysAPI.getAll = surveysAPI.list;

/* =========================
   Admin / Dashboard
   ========================= */
export const adminAPI = {
  getStats: () => unwrap(http.get('/admin/stats')),
};

/* =========================
   Debug helper
   ========================= */
export function logApiBase() {
  // eslint-disable-next-line no-console
  console.log('[API] Base URL:', API_BASE_RESOLVED, 'Root:', API_ROOT);
}

/* =========================
   Default export (compat)
   ========================= */
export default http;
