// src/pages/JobSearch.js
import React, { useEffect, useMemo, useState } from 'react';
import { Search, MapPin, Filter } from 'lucide-react';
import { jobsAPI } from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const JobSearch = () => {
  const [loading, setLoading] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filters, setFilters] = useState({
    q: '',
    jobType: '',
    location: '',
    category: '',      // <— UI single value
  });

  useEffect(() => {
    const load = async () => {
      try {
        setLoading(true);
        const [jobRes, catRes] = await Promise.all([
          jobsAPI.get('/jobs'),
          jobsAPI.get('/jobs/categories').catch(() => ({ data: { categories: [] } }))
        ]);
        setJobs(jobRes.data?.jobs || []);
        setCategories(catRes.data?.categories || []);
      } catch (e) {
        console.error(e);
        toast.error('Failed to load jobs');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const applySearch = async () => {
    try {
      setLoading(true);
      const params = {};
      if (filters.q) params.q = filters.q;
      if (filters.jobType) params.jobType = filters.jobType;
      if (filters.location) params.location = filters.location;
      if (filters.category) params.category = filters.category;   // <— key line

      const res = await jobsAPI.get('/jobs', { params });
      setJobs(res.data?.jobs || []);
    } catch (e) {
      console.error(e);
      toast.error('Search failed');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const clearFilters = async () => {
    setFilters({ q: '', jobType: '', location: '', category: '' });
    await applySearch();
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Find Jobs</h1>

      {/* Filters row - compact */}
      <div className="bg-white border border-gray-200 rounded-xl p-3 mb-4">
        <div className="grid grid-cols-1 sm:grid-cols-5 gap-2">
          <div className="sm:col-span-2">
            <div className="relative">
              <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
              <input
                name="q"
                value={filters.q}
                onChange={handleChange}
                className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
                placeholder="Search job title or keywords"
              />
            </div>
          </div>

          <div>
            <select
              name="jobType"
              value={filters.jobType}
              onChange={handleChange}
              className="w-full px-3 py-2 rounded-lg border border-gray-200"
              aria-label="Job type"
            >
              <option value="">Type</option>
              {['Internship','OJT','Part-time','Full-time','Contract'].map(t => (
                <option key={t} value={t}>{t}</option>
              ))}
            </select>
          </div>

          <div>
            <select
              name="category"
              value={filters.category}
              onChange={handleChange}
              className="w-full px-3 py-2 rounded-lg border border-gray-200"
              aria-label="Category"
            >
              <option value="">Category</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div>
            <div className="relative">
              <MapPin className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
              <input
                name="location"
                value={filters.location}
                onChange={handleChange}
                className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
                placeholder="Location"
              />
            </div>
          </div>
        </div>

        <div className="mt-3 flex items-center gap-2">
          <button
            onClick={applySearch}
            className="px-4 py-2 rounded-lg bg-blue-600 text-white"
          >
            Apply Filters
          </button>
          <button
            onClick={clearFilters}
            className="px-3 py-2 rounded-lg border"
          >
            Clear
          </button>
        </div>
      </div>

      {loading ? (
        <LoadingSpinner />
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {jobs.map(job => (
            <div key={job._id} className="bg-white border border-gray-200 rounded-xl p-4">
              <div className="flex items-start justify-between">
                <div>
                  <h3 className="font-semibold">{job.title}</h3>
                  <p className="text-sm text-gray-500">{job.company?.name} • {job.location}</p>
                </div>
                <span className="text-xs px-2 py-1 rounded bg-gray-100">{job.jobType}</span>
              </div>
              {job.categories?.length > 0 && (
                <div className="mt-2 text-xs text-gray-600">Category: {job.categories[0]}</div>
              )}
              <p className="mt-2 text-sm line-clamp-3">{job.shortDescription || job.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default JobSearch;
