// utils/api.js
import axios from 'axios';

// ---------------- Base URL ----------------
const viteUrl = import.meta?.env?.VITE_API_BASE_URL;
const craUrl = process.env?.REACT_APP_API_URL;

const API_BASE_URL = (viteUrl || craUrl || 'http://localhost:5000/api').replace(/\/+$/, '');

// ---------------- Axios instance ----------------
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: { 'Content-Type': 'application/json' },
  withCredentials: true, // ✅ allow cookies if needed
});

// ---------------- Interceptors ----------------
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
  },
  (error) => Promise.reject(error)
);

api.interceptors.response.use(
  (res) => res,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// ---------------- APIs ----------------
// AUTH
export const authAPI = {
  register: (data) => api.post('/auth/register', data),
  verifyOTP: (data) => api.post('/auth/verify-otp', data),
  resendOTP: (email) => api.post('/auth/resend-otp', { email }),
  login: (creds) => api.post('/auth/login', creds),
  forgotPassword: (email) => api.post('/auth/forgot-password', { email }),
  resetPassword: (data) => api.post('/auth/reset-password', data),
  getProfile: () => api.get('/auth/profile'),
};

// USERS
export const usersAPI = {
  getAll: (params) => api.get('/users', { params }),
  getById: (id) => api.get(`/users/${id}`),
  getMe: () => api.get('/users/me'),
  createUser: (data) => api.post('/users', data),
  updateProfile: (data) => api.put('/users/me', data),
  updateProfilePicture: (formData) =>
    api.post('/users/me/picture', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    }),
  deleteUser: (id) => api.delete(`/users/${id}`),
  getStatistics: () => api.get('/users/statistics'),
};

// COMPANIES
export const companiesAPI = {
  getAll: (params) => api.get('/companies', { params }),
  getById: (id) => api.get(`/companies/${id}`),
  create: (data) => api.post('/companies', data),
  update: (id, data) => api.put(`/companies/${id}`, data),
  delete: (id) => api.delete(`/companies/${id}`),
  updateLogo: (id, formData) =>
    api.post(`/companies/${id}/logo`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    }),
  getStatistics: () => api.get('/companies/admin/statistics'),
  search: (params) => api.get('/companies/search', { params }),
};

// JOBS
export const jobsAPI = {
  getAll: (params) => api.get('/jobs', { params }),
  getById: (id) => api.get(`/jobs/${id}`),
  create: (data) => api.post('/jobs', data),
  update: (id, data) => api.put(`/jobs/${id}`, data),
  delete: (id) => api.delete(`/jobs/${id}`),
  approve: (id) => api.patch(`/jobs/${id}/approve`),
  getStatistics: () => api.get('/jobs/admin/statistics'),
  search: (params) => api.get('/jobs/search', { params }),
  getFeatured: () => api.get('/jobs/featured'),
};

// SURVEYS
export const surveysAPI = {
  list: () => api.get('/surveys').then(r => r.data),
  get: (id) => api.get(`/surveys/${id}`).then(r => r.data),
  create: (payload) => api.post('/surveys', payload).then(r => r.data),
  update: (id, payload) => api.put(`/surveys/${id}`, payload).then(r => r.data),
  remove: (id) => api.delete(`/surveys/${id}`).then(r => r.data),
  ['delete']: (id) => api.delete(`/surveys/${id}`).then(r => r.data),

  responses: async (id, params = {}) => {
    if (!id) throw new Error('surveysAPI.responses: missing survey id');
    try {
      const r = await api.get(`/surveys/${id}/responses`, { params });
      return r.data;
    } catch (err) {
      if (err?.response?.status === 404) {
        const r2 = await api.get('/responses', { params: { surveyId: id, ...params } });
        return r2.data;
      }
      throw err;
    }
  },

  eligible: () => api.get('/surveys/active/eligible').then(r => r.data),
  eligibleActive: () => api.get('/surveys/active/eligible').then(r => r.data),
  submit: (id, payload) => api.post(`/surveys/${id}/responses`, payload).then(r => r.data),
};

// HEALTH
export const healthCheck = () => api.get('/health');

export default api;
