// src/pages/JobManagement.js
import React, { useEffect, useMemo, useState } from 'react';
import { Plus, X, Search } from 'lucide-react';
import { jobsAPI, companiesAPI } from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const defaultForm = {
  title: '',
  company: '',
  location: '',
  jobType: 'Internship',
  category: '',
  description: '',
  shortDescription: '',
  requirements: '',
  applicationDeadline: '',
  isRemote: false,
  isHybrid: false,
};

const JobManagement = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [jobs, setJobs] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [categories, setCategories] = useState([]);
  const [query, setQuery] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form, setForm] = useState(defaultForm);

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const [companiesRaw, jobsRaw, catsRaw] = await Promise.all([
          companiesAPI.list(),
          jobsAPI.list(),
          jobsAPI.listCategories().catch(() => []),
        ]);
        setCompanies(Array.isArray(companiesRaw) ? companiesRaw : companiesRaw?.companies || []);
        setJobs(Array.isArray(jobsRaw) ? jobsRaw : jobsRaw?.jobs || []);
        setCategories(Array.isArray(catsRaw) ? catsRaw : catsRaw?.categories || []);
      } catch (e) {
        console.error('Load error:', e);
        toast.error(e.message || 'Failed to load job data');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const filteredJobs = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return jobs;
    return jobs.filter(j =>
      j.title?.toLowerCase().includes(q) ||
      j.location?.toLowerCase().includes(q) ||
      j.jobType?.toLowerCase().includes(q) ||
      j.company?.name?.toLowerCase().includes(q) ||
      (Array.isArray(j.categories) && j.categories.some(c => c?.toLowerCase().includes(q)))
    );
  }, [jobs, query]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const openModal = () => { setForm(defaultForm); setIsModalOpen(true); };
  const closeModal = () => setIsModalOpen(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.company || !form.location || !form.jobType || !form.description) {
      toast.error('Please fill the required fields.');
      return;
    }
    try {
      setSaving(true);

      const requirementsArr = form.requirements
        ? form.requirements.split('\n').map(s => s.trim()).filter(Boolean)
        : [];

      const payload = {
        title: form.title.trim(),
        company: form.company,
        location: form.location.trim(),
        jobType: form.jobType,
        description: form.description.trim(),
        shortDescription: form.shortDescription?.trim() || undefined,
        requirements: requirementsArr,
        applicationDeadline: form.applicationDeadline || undefined,
        isRemote: !!form.isRemote,
        isHybrid: !!form.isHybrid,
        categories: form.category ? [form.category] : [],
        isApproved: true, // auto-approve
      };

      const createdRaw = await jobsAPI.create(payload);
      const createdJob = createdRaw?.job || createdRaw;
      setJobs(prev => [createdJob, ...prev]);

      if (form.category && !categories.includes(form.category)) {
        setCategories(prev => [...prev, form.category]);
      }

      toast.success('Job created');
      setIsModalOpen(false);
    } catch (e) {
      console.error('Create job error:', e);
      toast.error(e.message || 'Failed to add job');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-semibold">Job Management</h1>
          <p className="text-sm text-gray-500">Create and manage job postings</p>
        </div>
        <button onClick={openModal} className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-600 text-white">
          <Plus size={18} /> Add Job
        </button>
      </div>

      <div className="mb-4">
        <div className="relative w-full sm:w-96">
          <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
          <input
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
            placeholder="Search by title, company, location, type, or category"
          />
        </div>
      </div>

      <div className="bg-white rounded-xl border border-gray-200 overflow-x-auto">
        <table className="w-full text-sm">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-4 py-3 text-left">Job Title</th>
              <th className="px-4 py-3 text-left">Company</th>
              <th className="px-4 py-3 text-left">Location</th>
              <th className="px-4 py-3 text-left">Type</th>
              <th className="px-4 py-3 text-left">Category</th>
              <th className="px-4 py-3 text-left">Deadline</th>
              <th className="px-4 py-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredJobs.map(j => (
              <tr key={j._id} className="border-t">
                <td className="px-4 py-3">{j.title}</td>
                <td className="px-4 py-3">{j.company?.name || '-'}</td>
                <td className="px-4 py-3">{j.location || '-'}</td>
                <td className="px-4 py-3">{j.jobType || '-'}</td>
                <td className="px-4 py-3">{Array.isArray(j.categories) && j.categories[0] ? j.categories[0] : '-'}</td>
                <td className="px-4 py-3">{j.applicationDeadline ? new Date(j.applicationDeadline).toLocaleDateString() : '—'}</td>
                <td className="px-4 py-3 capitalize">{j.status || 'active'}</td>
              </tr>
            ))}
            {filteredJobs.length === 0 && (
              <tr>
                <td colSpan="7" className="px-4 py-6 text-center text-gray-500">No jobs found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 z-40 bg-black/40 flex items-center justify-center p-4">
          <div className="bg-white rounded-2xl w-full max-w-xl p-6 shadow-xl">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold">Add New Job</h2>
              <button onClick={closeModal} className="p-1 rounded hover:bg-gray-100">
                <X size={18} />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Job Title *</label>
                <input name="title" value={form.title} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2" />
              </div>

              <div>
                <label className="text-sm font-medium">Company *</label>
                <select name="company" value={form.company} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2">
                  <option value="">Select company</option>
                  {companies.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
                </select>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                <div>
                  <label className="text-sm font-medium">Job Type *</label>
                  <select name="jobType" value={form.jobType} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2">
                    {['Internship','OJT','Part-time','Full-time','Contract'].map(t => <option key={t} value={t}>{t}</option>)}
                  </select>
                </div>
                <div>
                  <label className="text-sm font-medium">Category</label>
                  <select name="category" value={form.category} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2">
                    <option value="">None</option>
                    {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                  </select>
                </div>
                <div>
                  <label className="text-sm font-medium">Location *</label>
                  <input name="location" value={form.location} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2" />
                </div>
              </div>

              <div>
                <label className="text-sm font-medium">Short Description</label>
                <input name="shortDescription" value={form.shortDescription} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2" />
              </div>

              <div>
                <label className="text-sm font-medium">Description *</label>
                <textarea name="description" value={form.description} onChange={handleChange} rows={4} className="mt-1 w-full border rounded-lg px-3 py-2" />
              </div>

              <div>
                <label className="text-sm font-medium">Requirements (one per line)</label>
                <textarea name="requirements" value={form.requirements} onChange={handleChange} rows={3} className="mt-1 w-full border rounded-lg px-3 py-2" />
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                <div>
                  <label className="text-sm font-medium">Application Deadline</label>
                  <input type="date" name="applicationDeadline" value={form.applicationDeadline} onChange={handleChange} className="mt-1 w-full border rounded-lg px-3 py-2" />
                </div>
                <label className="flex items-center gap-2 mt-6">
                  <input type="checkbox" name="isRemote" checked={form.isRemote} onChange={handleChange} />
                  <span className="text-sm">Remote</span>
                </label>
                <label className="flex items-center gap-2 mt-6">
                  <input type="checkbox" name="isHybrid" checked={form.isHybrid} onChange={handleChange} />
                  <span className="text-sm">Hybrid</span>
                </label>
              </div>

              <div className="flex items-center justify-end gap-2 pt-2">
                <button type="button" onClick={closeModal} className="px-4 py-2 rounded-lg border">Cancel</button>
                <button disabled={saving} className="px-4 py-2 rounded-lg bg-blue-600 text-white disabled:opacity-60">
                  {saving ? 'Saving…' : 'Add Job'}
                </button>
              </div>
            </form>

          </div>
        </div>
      )}
    </div>
  );
};

export default JobManagement;
