import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { authAPI } from '../utils/api';
import toast from 'react-hot-toast';

/* =========================
   Auth Context + Reducer
   ========================= */
const AuthContext = createContext();

const AUTH_ACTIONS = {
  SET_LOADING: 'SET_LOADING',
  LOGIN_SUCCESS: 'LOGIN_SUCCESS',
  LOGOUT: 'LOGOUT',
  UPDATE_USER: 'UPDATE_USER',
  SET_ERROR: 'SET_ERROR',
};

const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.SET_LOADING:
      return { ...state, loading: action.payload };

    case AUTH_ACTIONS.LOGIN_SUCCESS:
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        isAuthenticated: true,
        loading: false,
        error: null,
      };

    case AUTH_ACTIONS.LOGOUT:
      return {
        ...state,
        user: null,
        token: null,
        isAuthenticated: false,
        loading: false,
        error: null,
      };

    case AUTH_ACTIONS.UPDATE_USER:
      return {
        ...state,
        user: { ...state.user, ...action.payload },
      };

    case AUTH_ACTIONS.SET_ERROR:
      return { ...state, error: action.payload, loading: false };

    default:
      return state;
  }
};

const initialState = {
  user: null,
  token: null,
  isAuthenticated: false,
  loading: true,
  error: null,
};

/* =========================
   Provider
   ========================= */
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Boot: rehydrate from localStorage
  useEffect(() => {
    try {
      const token = localStorage.getItem('token');
      const rawUser = localStorage.getItem('user');

      if (token && rawUser) {
        const user = JSON.parse(rawUser);
        dispatch({
          type: AUTH_ACTIONS.LOGIN_SUCCESS,
          payload: { user, token },
        });
      } else {
        dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
      }
    } catch (e) {
      console.error('Auth rehydrate error:', e);
      logout();
    }

    // Helpful debug once on mount
    // eslint-disable-next-line no-console
    console.log('[Auth] API base:', import.meta.env.VITE_API_BASE_URL);
  }, []);

  /* ========== Helpers ========== */
  const storeSession = ({ user, token }) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
  };

  const clearSession = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  const normalizeErrorMessage = (error, fallback = 'Something went wrong') => {
    // Axios-style errors
    if (error?.response) {
      // Server responded but not 2xx
      return error.response.data?.message || fallback;
    }
    if (error?.request) {
      // Request made but no response (CORS, server down, wrong base URL)
      return 'Network error: cannot reach the API server';
    }
    // Anything else (thrown string, etc.)
    return error?.message || fallback;
  };

  /* ========== Actions ========== */

  // LOGIN
  const login = async (credentials) => {
    dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
    try {
      const res = await authAPI.login(credentials);

      // OTP flow (backend returns 403 + { requiresVerification, userId })
      if (res?.data?.requiresVerification) {
        dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
        toast('Email not verified. We sent you an OTP.', { icon: '✉️' });
        return { requiresVerification: true, userId: res.data.userId };
      }

      const { user, token } = res.data || {};
      if (!user || !token) {
        throw new Error('Malformed response from server');
      }

      storeSession({ user, token });

      dispatch({
        type: AUTH_ACTIONS.LOGIN_SUCCESS,
        payload: { user, token },
      });

      toast.success('Login successful!');
      return { success: true };
    } catch (err) {
      const msg = normalizeErrorMessage(err, 'Login failed');
      dispatch({ type: AUTH_ACTIONS.SET_ERROR, payload: msg });
      toast.error(msg);
      return { success: false, error: msg };
    }
  };

  // REGISTER
  const register = async (userData) => {
    dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
    try {
      const res = await authAPI.register(userData);

      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
      toast.success('Registered! Check your email for OTP.');
      return {
        success: true,
        requiresVerification: true,
        userId: res?.data?.userId,
      };
    } catch (err) {
      const msg = normalizeErrorMessage(err, 'Registration failed');
      dispatch({ type: AUTH_ACTIONS.SET_ERROR, payload: msg });
      toast.error(msg);
      return { success: false, error: msg };
    }
  };

  // VERIFY OTP
  const verifyOTP = async (email, otp) => {
    dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
    try {
      const res = await authAPI.verifyOTP({ email, otp });
      const { user, token } = res.data || {};
      if (!user || !token) {
        throw new Error('Malformed response from server');
      }

      storeSession({ user, token });

      dispatch({
        type: AUTH_ACTIONS.LOGIN_SUCCESS,
        payload: { user, token },
      });

      toast.success('Email verified successfully!');
      return { success: true };
    } catch (err) {
      const msg = normalizeErrorMessage(err, 'OTP verification failed');
      dispatch({ type: AUTH_ACTIONS.SET_ERROR, payload: msg });
      toast.error(msg);
      return { success: false, error: msg };
    }
  };

  // RESEND OTP
  const resendOTP = async (email) => {
    try {
      await authAPI.resendOTP({ email });
      toast.success('OTP sent!');
      return { success: true };
    } catch (err) {
      const msg = normalizeErrorMessage(err, 'Failed to resend OTP');
      toast.error(msg);
      return { success: false, error: msg };
    }
  };

  // LOGOUT
  const logout = () => {
    clearSession();
    dispatch({ type: AUTH_ACTIONS.LOGOUT });
    toast.success('Logged out');
  };

  // UPDATE USER (local cache)
  const updateUser = (userData) => {
    dispatch({ type: AUTH_ACTIONS.UPDATE_USER, payload: userData });
    try {
      const existing = JSON.parse(localStorage.getItem('user') || '{}');
      localStorage.setItem('user', JSON.stringify({ ...existing, ...userData }));
    } catch {
      // ignore
    }
  };

  // ROLE HELPERS
  const isAdmin = () => state.user?.userType === 'admin';
  const isStudentOrAlumni = () =>
    state.user?.userType === 'student' || state.user?.userType === 'alumni';

  const value = {
    ...state,
    login,
    register,
    verifyOTP,
    resendOTP,
    logout,
    updateUser,
    isAdmin,
    isStudentOrAlumni,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

/* =========================
   Hook
   ========================= */
export const useAuth = () => {
  const ctx = useContext(AuthContext);
  if (!ctx) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return ctx;
};
