// src/utils/api.js
// Axios client + API helpers with auto-detected /api prefix

import axios from 'axios';

/* =========================
   Base URL (host:port) only
   ========================= */
const API_BASE =
  (typeof import.meta !== 'undefined' && import.meta.env?.VITE_API_URL) ||
  process.env.REACT_APP_API_URL ||
  'http://localhost:5000';

function stripTrailingSlash(s) {
  return s ? s.replace(/\/+$/, '') : s;
}
const BASE = stripTrailingSlash(API_BASE);

/* =========================
   Dynamic prefix detection
   ========================= */
let API_PREFIX = '/api';       // assume /api first
let _prefixChecked = false;    // only check once

async function detectPrefix() {
  if (_prefixChecked) return API_PREFIX;
  _prefixChecked = true;

  const tryUrl = async (url) => {
    try {
      const res = await fetch(url, { credentials: 'include' });
      return res.ok;
    } catch {
      return false;
    }
  };

  // Try /api/health then /health
  const withApi = `${BASE}/api/health`;
  const noApi = `${BASE}/health`;

  if (await tryUrl(withApi)) {
    API_PREFIX = '/api';
  } else if (await tryUrl(noApi)) {
    API_PREFIX = '';
  } else {
    // Leave default, but log for debugging
    console.warn('[API] Could not verify health route at', withApi, 'or', noApi);
  }
  console.log('[API] Using prefix:', API_PREFIX || '(none)');
  return API_PREFIX;
}

/* =========================
   Build URL helper
   ========================= */
function fullPath(path) {
  const pp = path.startsWith('/') ? path : `/${path}`;
  const pref = API_PREFIX || '';
  return `${BASE}${pref}${pp}`;
}

/* =========================
   Axios instance
   ========================= */
export const http = axios.create({
  baseURL: `${BASE}/api`, // default; we may override per-request below
  withCredentials: true,
  headers: { 'Content-Type': 'application/json' },
});

/* Override the request adapter to remap URL with detected prefix */
http.interceptors.request.use(async (config) => {
  // Ensure prefix is detected before first outbound call
  await detectPrefix();

  // If config.url is relative, rebuild it with the detected prefix
  // (We keep axios baseURL for absolute-URL safety, but we’ll compose ourselves.)
  if (config.url && !/^https?:\/\//i.test(config.url)) {
    const composed = fullPath(config.url);
    // Use absolute URL to avoid baseURL mismatch
    config.baseURL = undefined;
    config.url = composed;
  }
  return config;
});

/* =========================
   Error normalization
   ========================= */
http.interceptors.response.use(
  (res) => res,
  (error) => {
    const status = error?.response?.status;
    const data = error?.response?.data;
    const message =
      data?.message ||
      data?.error ||
      error?.message ||
      `HTTP ${status || 'ERR'}`;

    error.normalizedMessage = message;
    return Promise.reject(error);
  }
);

const unwrap = (p) =>
  p.then((r) => r.data).catch((e) => {
    // Show the exact URL that failed (useful for 404s)
    const failedUrl = e?.config?.url || '(unknown URL)';
    const msg = e.normalizedMessage || e.message || 'Request failed';
    throw new Error(`${msg} @ ${failedUrl}`);
  });

/* =========================
   Token helper
   ========================= */
export function setAuthToken(token) {
  if (token) http.defaults.headers.common.Authorization = `Bearer ${token}`;
  else delete http.defaults.headers.common.Authorization;
}

/* =========================
   Health
   ========================= */
export const healthAPI = {
  ping: () => unwrap(http.get('/health')),
};

/* =========================
   Auth
   ========================= */
export const authAPI = {
  login: (credentials) => unwrap(http.post('/auth/login', credentials)),
  me: () => unwrap(http.get('/auth/me')),
  logout: () => unwrap(http.post('/auth/logout')),
};

/* =========================
   Jobs
   ========================= */
export const jobsAPI = {
  list: (params = {}) => unwrap(http.get('/jobs', { params })),
  get: (id) => unwrap(http.get(`/jobs/${id}`)),
  create: (payload) => unwrap(http.post('/jobs', payload)),
  update: (id, payload) => unwrap(http.put(`/jobs/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/jobs/${id}`)),
};

/* =========================
   Companies
   ========================= */
export const companiesAPI = {
  list: (params = {}) => unwrap(http.get('/companies', { params })),
  get: (id) => unwrap(http.get(`/companies/${id}`)),
  create: (payload) => unwrap(http.post('/companies', payload)),
  update: (id, payload) => unwrap(http.put(`/companies/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/companies/${id}`)),
};

/* =========================
   Users (Admin)
   ========================= */
export const usersAPI = {
  list: (params = {}) => unwrap(http.get('/users', { params })),
  get: (id) => unwrap(http.get(`/users/${id}`)),
  create: (payload) => unwrap(http.post('/users', payload)),
  update: (id, payload) => unwrap(http.put(`/users/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/users/${id}`)),
};

/* =========================
   Surveys
   ========================= */
export const surveysAPI = {
  list: (params = {}) => unwrap(http.get('/surveys', { params })),
  get: (id) => unwrap(http.get(`/surveys/${id}`)),
  create: (payload) => unwrap(http.post('/surveys', payload)),
  update: (id, payload) => unwrap(http.put(`/surveys/${id}`, payload)),
  remove: (id) => unwrap(http.delete(`/surveys/${id}`)),
  listResponses: (surveyId, params = {}) =>
    unwrap(http.get(`/surveys/${surveyId}/responses`, { params })),
};

/* =========================
   Survey Responses (flat)
   ========================= */
export const surveyResponsesAPI = {
  list: (params = {}) => unwrap(http.get('/survey-responses', { params })),
  get: (id) => unwrap(http.get(`/survey-responses/${id}`)),
};

/* =========================
   Admin
   ========================= */
export const adminAPI = {
  stats: () => unwrap(http.get('/admin/stats')),
  activity: (params = {}) => unwrap(http.get('/admin/activity', { params })),
};

/* =========================
   Debug helper
   ========================= */
export function logApiBase() {
  console.log('[API] Host:', BASE, 'Prefix:', API_PREFIX || '(none)');
}

export const API_ROOT = () => `${BASE}${API_PREFIX || ''}`;

/* Default export (for legacy imports): api.get('/path') */
export default http;
