import React, { useEffect, useState } from 'react';
import { surveysAPI } from '../../utils/api';
import { useParams } from 'react-router-dom';
import { useNavigate, Link } from 'react-router-dom';
import { ArrowLeft } from 'lucide-react';
import LoadingSpinner from '../../components/LoadingSpinner';

export default function SurveyResponses() {
  const { id } = useParams();
  const [survey, setSurvey] = useState(null);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({ role: '' });

  const load = async () => {
    setLoading(true);
    const s = await surveysAPI.get(id);
    setSurvey(s);
    const data = await surveysAPI.responses(id, { role: filters.role || undefined });
    setResponses(data);
    setLoading(false);
  };

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [id, filters]);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="max-w-5xl mx-auto p-4">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-2xl font-bold">Responses — {survey.title}</h1>
        <div className="flex items-center gap-2">
          <select className="input" value={filters.role} onChange={e=>setFilters(f=>({ ...f, role: e.target.value }))}>
            <option value="">All Roles</option>
            <option value="student">Students</option>
            <option value="alumni">Alumni</option>
          </select>
        </div>
      </div>

      <div className="grid gap-4">
        {responses.map(r => (
          <div key={r._id} className="card">
            <div className="card-body">
              <div className="text-sm opacity-70 mb-2">
                <b>{r.userId?.name || r.userId?.email}</b> • {r.role} • {new Date(r.createdAt).toLocaleString()}
              </div>
              <div className="grid gap-3">
                {survey.questions.map(q => {
                  const ans = r.answers.find(a => String(a.questionId) === String(q._id));
                  return (
                    <div key={q._id}>
                      <div className="font-medium">Q: {q.text} {q.required && <span className="text-red-500">*</span>}</div>
                      <div className="opacity-80">
                        {Array.isArray(ans?.value) ? ans.value.join(', ') : (ans?.value ?? <i className="opacity-50">—</i>)}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        ))}
        {!responses.length && <div className="text-center opacity-70">No responses yet.</div>}
      </div>
    </div>
  );
}
