// src/contexts/AuthContext.js
import React, { createContext, useContext, useEffect, useReducer } from 'react';
import { authAPI, setAuthToken } from '../utils/api';
import toast from 'react-hot-toast';

const AuthContext = createContext();

const ACTIONS = {
  SET_LOADING: 'SET_LOADING',
  LOGIN_SUCCESS: 'LOGIN_SUCCESS',
  LOGOUT: 'LOGOUT',
  UPDATE_USER: 'UPDATE_USER',
  SET_ERROR: 'SET_ERROR',
};

const initial = {
  user: null,
  token: null,
  isAuthenticated: false,
  loading: true,
  error: null,
};

function reducer(state, action) {
  switch (action.type) {
    case ACTIONS.SET_LOADING:
      return { ...state, loading: action.payload };
    case ACTIONS.LOGIN_SUCCESS:
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        isAuthenticated: true,
        loading: false,
        error: null,
      };
    case ACTIONS.UPDATE_USER:
      return { ...state, user: action.payload };
    case ACTIONS.LOGOUT:
      return { ...initial, loading: false };
    case ACTIONS.SET_ERROR:
      return { ...state, error: action.payload, loading: false };
    default:
      return state;
  }
}

function normalizeError(err, fallback = 'Something went wrong') {
  if (err?.message) return err.message;
  return fallback;
}

export function AuthProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initial);

  // Rehydrate token on refresh and fetch /auth/me
  useEffect(() => {
    const t = localStorage.getItem('token');
    if (t) {
      setAuthToken(t);
      authAPI
        .me()
        .then((me) =>
          dispatch({ type: ACTIONS.LOGIN_SUCCESS, payload: { user: me, token: t } })
        )
        .catch(() => {
          // token invalid/expired
          localStorage.removeItem('token');
          setAuthToken(null);
          dispatch({ type: ACTIONS.SET_LOADING, payload: false });
        });
    } else {
      dispatch({ type: ACTIONS.SET_LOADING, payload: false });
    }
  }, []);

  async function login({ email, password }) {
    dispatch({ type: ACTIONS.SET_LOADING, payload: true });
    try {
      // api.js returns the JSON directly (not {data: ...})
      const { user, token } = await authAPI.login({ email, password });
      localStorage.setItem('token', token);
      setAuthToken(token);
      dispatch({ type: ACTIONS.LOGIN_SUCCESS, payload: { user, token } });
      toast.success('Logged in!');
      return { success: true, user };
    } catch (err) {
      const msg = normalizeError(err, 'Login failed');
      dispatch({ type: ACTIONS.SET_ERROR, payload: msg });
      toast.error(msg);
      return { success: false, error: msg };
    }
  }

  function logout(showToast = true) {
    // we use bearer token in headers; clearing it is enough
    localStorage.removeItem('token');
    setAuthToken(null);
    dispatch({ type: ACTIONS.LOGOUT });
    if (showToast) toast('Logged out');
  }

  async function refreshMe() {
    try {
      const me = await authAPI.me();
      dispatch({ type: ACTIONS.UPDATE_USER, payload: me });
      return me;
    } catch (e) {
      // If this fails, silently keep current state
      return null;
    }
  }

  const value = {
    ...state,
    login,
    logout,
    refreshMe,
    // If you later add register/OTP on the backend, wire them here.
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error('useAuth must be used within an AuthProvider');
  return ctx;
}
