// src/pages/JobSearch.js
import React, { useEffect, useMemo, useRef, useState } from 'react';
import { jobsAPI, http } from '../utils/api';
import { Search, SlidersHorizontal, Briefcase, MapPin, Loader2 } from 'lucide-react';

const DEBOUNCE_MS = 400;

function JobCard({ job }) {
  const title = job.title || job.position || 'Untitled role';
  const company = job.companyName || job.company?.name || 'â€”';
  const where = job.location || job.city || job.country || 'Unspecified location';
  const desc = (job.description || '').replace(/\s+/g, ' ').trim();

  return (
    <div className="card p-5 h-full flex flex-col">
      <div className="flex items-start justify-between gap-3">
        <div>
          <h3 className="text-base font-semibold text-gray-900">{title}</h3>
          <p className="text-sm text-gray-600">{company}</p>
          <p className="mt-1 text-xs text-gray-500 flex items-center gap-1">
            <MapPin className="w-3.5 h-3.5" />
            {where}
          </p>
        </div>
        <div className="shrink-0 rounded-md bg-primary-50 p-2">
          <Briefcase className="w-5 h-5 text-primary-600" />
        </div>
      </div>

      {desc && <p className="mt-3 text-sm text-gray-700 line-clamp-3">{desc}</p>}

      <div className="mt-auto pt-4">
        <button className="btn btn-ghost btn-sm">View</button>
      </div>
    </div>
  );
}

export default function JobSearch() {
  // Query/UI state
  const [q, setQ] = useState('');
  const [type, setType] = useState('');
  const [location, setLocation] = useState('');
  const [category, setCategory] = useState('');
  const [sort, setSort] = useState('recent');
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);

  // Data state
  const [jobs, setJobs] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  // Categories (for dropdown)
  const [categories, setCategories] = useState([]);

  // debounce & guard
  const timerRef = useRef(null);
  const lastReqRef = useRef(0);

  const params = useMemo(
    () => ({
      q: q.trim() || undefined,
      type: type || undefined,
      location: location || undefined,
      category: category || undefined,
      sort: sort || undefined,
      page,
      limit,
    }),
    [q, type, location, category, sort, page, limit]
  );

  async function fetchJobs(activeReqId, p = params) {
    try {
      setLoading(true);
      setErr(null);
      const result = await jobsAPI.list(p);

      if (activeReqId !== lastReqRef.current) return;

      if (Array.isArray(result)) {
        setJobs(result);
        setTotal(result.length);
      } else {
        setJobs(result?.items || []);
        setTotal(Number(result?.total || 0));
      }
    } catch (e) {
      if (activeReqId !== lastReqRef.current) return;
      setErr(e.message || 'Failed to load jobs');
      setJobs([]);
      setTotal(0);
    } finally {
      if (activeReqId === lastReqRef.current) setLoading(false);
    }
  }

  // Debounced fetch on filter changes
  useEffect(() => {
    const reqId = Date.now();
    lastReqRef.current = reqId;
    if (timerRef.current) clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => fetchJobs(reqId), DEBOUNCE_MS);
    return () => clearTimeout(timerRef.current);
  }, [q, type, location, category, sort, page, limit]);

  function onSubmit(e) {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);
    const reqId = Date.now();
    lastReqRef.current = reqId;
    setPage(1);
    fetchJobs(reqId, { ...params, page: 1 });
  }

  function resetFilters() {
    setQ('');
    setType('');
    setLocation('');
    setCategory('');
    setSort('recent');
    setPage(1);
  }

  const totalPages = Math.max(1, Math.ceil(total / limit));

  // --- Load category options ---
  useEffect(() => {
    let mounted = true;

    async function loadCategories() {
      try {
        // Preferred: a dedicated endpoint
        const res = await http.get('/jobs/categories'); // expects array of strings
        if (!mounted) return;
        const list = Array.isArray(res.data) ? res.data : res.data?.categories;
        if (Array.isArray(list)) {
          setCategories(list.filter(Boolean));
          return;
        }
        throw new Error('No categories endpoint');
      } catch {
        // Fallback: derive from existing jobs if the endpoint isn't implemented yet
        try {
          const all = await jobsAPI.list({ limit: 1000 });
          const items = Array.isArray(all) ? all : all?.items || [];
          const uniq = Array.from(
            new Set(
              items
                .map((j) => j.category)
                .flat()
                .filter(Boolean)
            )
          ).sort((a, b) => String(a).localeCompare(String(b)));
          if (mounted) setCategories(uniq);
        } catch {
          if (mounted) setCategories([]);
        }
      }
    }

    loadCategories();
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-heading font-bold text-gray-900 mb-2">
        Find Your Dream Opportunity
      </h1>
      <p className="text-gray-600 mb-6">
        Discover OJT/internship, and job opportunities in the aerospace industry
      </p>

      {/* Row 1: Search + buttons */}
      <form onSubmit={onSubmit} className="flex flex-wrap gap-3 items-center">
        <div className="relative flex-1 min-w-[260px]">
          <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400">
            <Search className="w-5 h-5" />
          </span>
          <input
            type="text"
            className="input w-full pl-10 h-11"
            placeholder="Search job title, company, keywordâ€¦"
            value={q}
            onChange={(e) => {
              setPage(1);
              setQ(e.target.value);
            }}
          />
        </div>

        <button type="button" className="btn btn-ghost h-11">
          <SlidersHorizontal className="w-4 h-4 mr-1" />
          Filters
        </button>

        <button type="submit" className="btn btn-primary h-11">
          <Search className="w-4 h-4 mr-1" />
          Search
        </button>
      </form>

      {/* Row 2: Filters (location & category made smaller) */}
      <div className="mt-4 flex flex-wrap gap-3 items-center">
        <select
          value={type}
          onChange={(e) => {
            setPage(1);
            setType(e.target.value);
          }}
          className="input h-10 min-w-[140px]"
        >
          <option value="">All Types</option>
          <option value="internship">Internship / OJT</option>
          <option value="fulltime">Full-time</option>
          <option value="parttime">Part-time</option>
          <option value="contract">Contract</option>
        </select>

        {/* Smaller Location input */}
        <input
          className="input h-10 w-[180px]"
          placeholder="Location"
          value={location}
          onChange={(e) => {
            setPage(1);
            setLocation(e.target.value);
          }}
        />

        {/* Smaller Category dropdown (from admin-added categories) */}
        <select
          className="input h-10 w-[200px]"
          value={category}
          onChange={(e) => {
            setPage(1);
            setCategory(e.target.value);
          }}
        >
          <option value="">All categories</option>
          {categories.map((c) => (
            <option key={String(c)} value={c}>
              {c}
            </option>
          ))}
        </select>

        <select
          value={sort}
          onChange={(e) => {
            setPage(1);
            setSort(e.target.value);
          }}
          className="input h-10 min-w-[140px]"
        >
          <option value="recent">Most recent</option>
          <option value="a-z">Title (Aâ€“Z)</option>
        </select>

        <div className="ml-auto flex items-center gap-3">
          <button type="button" onClick={resetFilters} className="btn btn-ghost h-10">
            Reset Filters
          </button>
          <div className="flex items-center gap-2 text-sm text-gray-600">
            <span>Per page</span>
            <select
              value={limit}
              onChange={(e) => {
                setPage(1);
                setLimit(Number(e.target.value));
              }}
              className="input h-10 w-[84px]"
            >
              <option value={10}>10</option>
              <option value={20}>20</option>
              <option value={50}>50</option>
            </select>
          </div>
        </div>
      </div>

      {/* Results header */}
      <div className="flex items-center justify-between mt-6">
        <div className="text-sm text-gray-600 flex items-center gap-2">
          {loading && <Loader2 className="w-4 h-4 animate-spin text-gray-400" />}
          {loading ? 'Loadingâ€¦' : `${total} jobs found`}
          {err ? <span className="text-error-600 ml-2">{err}</span> : null}
        </div>
      </div>

      {/* Results */}
      {!loading && !err && jobs.length === 0 ? (
        <div className="py-16 text-center text-gray-500">
          <Briefcase className="w-12 h-12 mx-auto mb-3 opacity-60" />
          <div className="text-lg font-medium">No jobs found</div>
          <div className="text-sm">Try adjusting your search criteria</div>
        </div>
      ) : (
        <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4">
          {jobs.map((j) => (
            <JobCard key={j._id || j.id} job={j} />
          ))}
        </div>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="mt-6 flex items-center justify-center gap-2">
          <button
            className="btn btn-ghost btn-sm"
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            disabled={page <= 1}
          >
            Prev
          </button>
          <span className="text-sm text-gray-600">
            Page <strong>{page}</strong> of <strong>{totalPages}</strong>
          </span>
          <button
            className="btn btn-ghost btn-sm"
            onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
            disabled={page >= totalPages}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}
