import React, { useEffect, useMemo, useState } from 'react';
import { surveysAPI } from '../../utils/api';
import { useNavigate, useParams } from 'react-router-dom';
import { Save, PlusCircle, GripVertical, Trash2 } from 'lucide-react';
import toast from 'react-hot-toast';
import LoadingSpinner from '../../components/LoadingSpinner';

const emptyQ = (order) => ({ text: '', type: 'text', options: [], required: false, order });

export default function SurveyBuilder() {
  const { id } = useParams(); // undefined when /new
  const isEdit = Boolean(id);
  const nav = useNavigate();

  const [loading, setLoading] = useState(isEdit);
  const [saving, setSaving] = useState(false);
  const [form, setForm] = useState({
    title: '',
    description: '',
    isActive: true,
    audience: 'all',
    questions: [emptyQ(0)]
  });

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      try {
        const data = await surveysAPI.get(id);
        setForm({
          title: data.title,
          description: data.description || '',
          isActive: data.isActive,
          audience: data.audience || 'all',
          questions: data.questions?.length ? data.questions.sort((a,b)=>a.order-b.order) : [emptyQ(0)]
        });
      } catch {
        toast.error('Failed to load survey');
      } finally { setLoading(false); }
    };
    if (isEdit) load();
  }, [id, isEdit]);

  const addQ = () => {
    setForm(f => ({ ...f, questions: [...f.questions, emptyQ(f.questions.length)] }));
  };

  const rmQ = (idx) => {
    setForm(f => ({ ...f, questions: f.questions.filter((_,i)=>i!==idx).map((q,i)=>({ ...q, order: i })) }));
  };

  const setQ = (idx, patch) => {
    setForm(f => {
      const qs = f.questions.slice();
      qs[idx] = { ...qs[idx], ...patch };
      return { ...f, questions: qs };
    });
  };

  const save = async () => {
    if (!form.title.trim()) return toast.error('Title is required');
    if (!form.questions.length) return toast.error('Add at least one question');
    if (form.questions.some(q => q.required && !q.text.trim())) return toast.error('Required questions need text');

    setSaving(true);
    try {
      const payload = { ...form, questions: form.questions.map((q,i)=>({ ...q, order: i })) };
      if (isEdit) await surveysAPI.update(id, payload);
      else await surveysAPI.create(payload);
      toast.success('Survey saved');
      nav('/admin/surveys');
    } catch (e) {
      toast.error(e?.response?.data?.error || 'Failed to save');
    } finally { setSaving(false); }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-2xl font-bold">{isEdit ? 'Edit Survey' : 'New Survey'}</h1>
        <button className="btn btn-primary flex items-center gap-2" disabled={saving} onClick={save}><Save size={18}/> {saving ? 'Saving...' : 'Save'}</button>
      </div>

      <div className="card mb-4">
        <div className="card-body grid md:grid-cols-2 gap-4">
          <div>
            <label className="label">Title</label>
            <input className="input" value={form.title} onChange={e=>setForm({...form, title:e.target.value})}/>
          </div>
          <div>
            <label className="label">Audience</label>
            <select className="input" value={form.audience} onChange={e=>setForm({...form, audience:e.target.value})}>
              <option value="all">All</option>
              <option value="students">Students</option>
              <option value="alumni">Alumni</option>
            </select>
          </div>
          <div className="md:col-span-2">
            <label className="label">Description</label>
            <textarea className="input" rows={3} value={form.description} onChange={e=>setForm({...form, description:e.target.value})}/>
          </div>
          <div>
            <label className="label">Status</label>
            <select className="input" value={form.isActive ? 'active':'inactive'} onChange={e=>setForm({...form, isActive:e.target.value==='active'})}>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>
        </div>
      </div>

      <div className="flex items-center justify-between mb-2">
        <h2 className="text-xl font-semibold">Questions</h2>
        <button className="btn btn-outline flex items-center gap-2" onClick={addQ}><PlusCircle size={18}/> Add question</button>
      </div>

      <div className="grid gap-3">
        {form.questions.map((q, idx) => (
          <div className="card" key={idx}>
            <div className="card-body">
              <div className="flex items-center justify-between mb-2">
                <div className="flex items-center gap-2"><GripVertical size={16}/> <b>Q{idx+1}</b></div>
                <button className="btn btn-ghost text-red-500 flex items-center gap-1" onClick={()=>rmQ(idx)}><Trash2 size={16}/> Remove</button>
              </div>
              <div className="grid md:grid-cols-2 gap-3">
                <div>
                  <label className="label">Question Text</label>
                  <input className="input" value={q.text} onChange={e=>setQ(idx,{ text:e.target.value })}/>
                </div>
                <div>
                  <label className="label">Type</label>
                  <select className="input" value={q.type} onChange={e=>setQ(idx,{ type:e.target.value, options: (e.target.value==='text'||e.target.value==='textarea')?[]:q.options })}>
                    <option value="text">Short Text</option>
                    <option value="textarea">Long Text</option>
                    <option value="radio">Single Choice</option>
                    <option value="checkbox">Multiple Choice</option>
                  </select>
                </div>
                <div>
                  <label className="label">Required</label>
                  <select className="input" value={q.required ? 'yes':'no'} onChange={e=>setQ(idx,{ required: e.target.value==='yes' })}>
                    <option value="no">No</option>
                    <option value="yes">Yes</option>
                  </select>
                </div>
                {(q.type === 'radio' || q.type === 'checkbox') && (
                  <div className="md:col-span-2">
                    <label className="label">Options (comma separated)</label>
                    <input className="input" value={q.options?.join(', ') || ''} onChange={e=>setQ(idx,{ options: e.target.value.split(',').map(s=>s.trim()).filter(Boolean) })}/>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
