// src/pages/JobSearch.js
import React, { useEffect, useMemo, useState } from 'react';
import { Search } from 'lucide-react';
import { jobsAPI } from '../utils/api';
import toast from 'react-hot-toast';

const JobSearch = () => {
  const [loading, setLoading] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [q, setQ] = useState('');
  const [type, setType] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const [jobsRaw, catsRaw] = await Promise.all([
          jobsAPI.list({ approvedOnly: true, status: 'active' }),
          jobsAPI.listCategories().catch(() => []),
        ]);
        setJobs(Array.isArray(jobsRaw) ? jobsRaw : jobsRaw?.jobs || []);
        setCategories(Array.isArray(catsRaw) ? catsRaw : catsRaw?.categories || []);
      } catch (e) {
        console.error('JobSearch load error:', e);
        toast.error(e.message || 'Failed to load jobs');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const filtered = useMemo(() => {
    const _q = q.trim().toLowerCase();
    return jobs.filter(j => {
      const byQ =
        !_q ||
        j.title?.toLowerCase().includes(_q) ||
        j.description?.toLowerCase().includes(_q) ||
        j.company?.name?.toLowerCase().includes(_q);
      const byType = !type || j.jobType === type;
      const byCat = !category || (Array.isArray(j.categories) && j.categories.includes(category));
      const byLoc = !location || j.location?.toLowerCase().includes(location.toLowerCase());
      return byQ && byType && byCat && byLoc;
    });
  }, [jobs, q, type, category, location]);

  const applyFilters = async () => {
    try {
      setLoading(true);
      const params = {
        approvedOnly: true,
        status: 'active',
        q: q || undefined,
        jobType: type || undefined,
        category: category || undefined,
        location: location || undefined,
      };
      const raw = await jobsAPI.list(params);
      setJobs(Array.isArray(raw) ? raw : raw?.jobs || []);
    } catch (e) {
      console.error('Apply filter error:', e);
      toast.error(e.message || 'Failed to load jobs');
    } finally {
      setLoading(false);
    }
  };

  const clearFilters = () => {
    setQ('');
    setType('');
    setCategory('');
    setLocation('');
    applyFilters();
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Find Jobs</h1>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-3 mb-4">
        <div className="relative flex-1 min-w-[220px]">
          <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
            placeholder="Search job title or keywords"
          />
        </div>

        <select value={type} onChange={(e) => setType(e.target.value)} className="px-3 py-2 rounded-lg border border-gray-200">
          <option value="">Type</option>
          {['Internship', 'OJT', 'Part-time', 'Full-time', 'Contract'].map(t => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>

        <select value={category} onChange={(e) => setCategory(e.target.value)} className="px-3 py-2 rounded-lg border border-gray-200">
          <option value="">Category</option>
          {categories.map(c => <option key={c} value={c}>{c}</option>)}
        </select>

        <input
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="px-3 py-2 rounded-lg border border-gray-200"
          placeholder="Location"
        />

        <button onClick={applyFilters} className="px-4 py-2 rounded-lg bg-blue-600 text-white">
          Apply Filters
        </button>
        <button onClick={clearFilters} className="px-4 py-2 rounded-lg border">
          Clear
        </button>
      </div>

      {/* Results */}
      {loading ? (
        <div className="text-gray-500">Loading…</div>
      ) : filtered.length === 0 ? (
        <div className="text-gray-500">No jobs found.</div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2">
          {filtered.map((j) => (
            <div key={j._id} className="rounded-xl border p-4">
              <div className="font-semibold">{j.title}</div>
              <div className="text-sm text-gray-600">{j.company?.name || '—'} • {j.location || '—'}</div>
              <div className="text-sm mt-1">{j.jobType} {j.categories?.[0] ? `• ${j.categories[0]}` : ''}</div>
              {j.shortDescription ? (
                <p className="text-sm mt-2 text-gray-700 line-clamp-3">{j.shortDescription}</p>
              ) : (
                <p className="text-sm mt-2 text-gray-700 line-clamp-3">{j.description}</p>
              )}
              {j.applicationDeadline && (
                <div className="text-xs text-gray-500 mt-2">
                  Deadline: {new Date(j.applicationDeadline).toLocaleDateString()}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default JobSearch;
