// src/pages/CompanyManagement.js
import React, { useEffect, useMemo, useState } from 'react';
import { Plus, X, Search } from 'lucide-react';
import { companiesAPI } from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const defaultForm = {
  name: '',
  website: '',
  email: '',
  phone: '',
  location: '',
  description: '',
};

const CompanyManagement = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [companies, setCompanies] = useState([]);
  const [query, setQuery] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form, setForm] = useState(defaultForm);

  // Load companies (defensive for array or { companies: [] })
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const raw = await companiesAPI.list();
        const list = Array.isArray(raw) ? raw : raw?.companies || [];
        setCompanies(list);
      } catch (e) {
        console.error('Companies load error:', e);
        toast.error(e.message || 'Failed to load companies');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return companies;
    return companies.filter(c =>
      c?.name?.toLowerCase().includes(q) ||
      c?.location?.toLowerCase().includes(q) ||
      c?.website?.toLowerCase().includes(q) ||
      c?.email?.toLowerCase().includes(q)
    );
  }, [companies, query]);

  const openModal = () => { setForm(defaultForm); setIsModalOpen(true); };
  const closeModal = () => setIsModalOpen(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name.trim()) {
      toast.error('Company name is required');
      return;
    }
    try {
      setSaving(true);
      const payload = {
        name: form.name.trim(),
        website: form.website?.trim() || undefined,
        email: form.email?.trim() || undefined,
        phone: form.phone?.trim() || undefined,
        location: form.location?.trim() || undefined,
        description: form.description?.trim() || undefined,
      };
      const createdRaw = await companiesAPI.create(payload);
      const created = createdRaw?.company || createdRaw; // handle either shape
      setCompanies(prev => [created, ...prev]);
      toast.success('Company created');
      setIsModalOpen(false);
    } catch (e) {
      console.error('Create company error:', e);
      toast.error(e.message || 'Failed to add company');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-semibold">Company Management</h1>
          <p className="text-sm text-gray-500">Create and manage partner companies</p>
        </div>
        <button
          onClick={openModal}
          className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-700"
        >
          <Plus size={18} />
          Add Company
        </button>
      </div>

      {/* Search */}
      <div className="mb-4">
        <div className="relative w-full sm:w-96">
          <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
          <input
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
            placeholder="Search companies by name, location, website, or email"
          />
        </div>
      </div>

      {/* Companies table */}
      <div className="bg-white rounded-xl border border-gray-200 overflow-x-auto">
        <table className="w-full text-sm">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-4 py-3 text-left">Name</th>
              <th className="px-4 py-3 text-left">Location</th>
              <th className="px-4 py-3 text-left">Website</th>
              <th className="px-4 py-3 text-left">Email</th>
              <th className="px-4 py-3 text-left">Phone</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(c => (
              <tr key={c._id} className="border-t">
                <td className="px-4 py-3">{c.name || '-'}</td>
                <td className="px-4 py-3">{c.location || '-'}</td>
                <td className="px-4 py-3">
                  {c.website ? (
                    <a className="text-blue-600 hover:underline" href={/^https?:\/\//.test(c.website) ? c.website : `https://${c.website}`} target="_blank" rel="noreferrer">
                      {c.website}
                    </a>
                  ) : '—'}
                </td>
                <td className="px-4 py-3">{c.email || '—'}</td>
                <td className="px-4 py-3">{c.phone || '—'}</td>
              </tr>
            ))}
            {filtered.length === 0 && (
              <tr>
                <td colSpan="5" className="px-4 py-6 text-center text-gray-500">No companies found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Add Company Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-40">
          <div className="bg-white rounded-2xl w-full max-w-xl p-6 shadow-xl">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold">Add New Company</h2>
              <button onClick={closeModal} className="p-1 rounded hover:bg-gray-100">
                <X size={18} />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Company Name *</label>
                <input
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                  placeholder="e.g., Acme Corporation"
                />
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                <div>
                  <label className="text-sm font-medium">Location</label>
                  <input
                    name="location"
                    value={form.location}
                    onChange={handleChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                    placeholder="e.g., Quezon City"
                  />
                </div>
                <div>
                  <label className="text-sm font-medium">Website</label>
                  <input
                    name="website"
                    value={form.website}
                    onChange={handleChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                    placeholder="example.com"
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                <div>
                  <label className="text-sm font-medium">Email</label>
                  <input
                    name="email"
                    type="email"
                    value={form.email}
                    onChange={handleChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                    placeholder="hr@example.com"
                  />
                </div>
                <div>
                  <label className="text-sm font-medium">Phone</label>
                  <input
                    name="phone"
                    value={form.phone}
                    onChange={handleChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                    placeholder="+63 ..."
                  />
                </div>
              </div>

              <div>
                <label className="text-sm font-medium">Description</label>
                <textarea
                  name="description"
                  value={form.description}
                  onChange={handleChange}
                  rows={3}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                  placeholder="Brief company overview"
                />
              </div>

              <div className="flex items-center justify-end gap-2 pt-2">
                <button type="button" onClick={closeModal} className="px-4 py-2 rounded-lg border">
                  Cancel
                </button>
                <button
                  disabled={saving}
                  className="px-4 py-2 rounded-lg bg-blue-600 text-white disabled:opacity-60"
                >
                  {saving ? 'Saving…' : 'Add Company'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CompanyManagement;
