import React, { useState, useEffect } from 'react';
import { 
  Search, 
  Filter, 
  MapPin, 
  Briefcase, 
  Calendar,
  Building2,
  Clock,
  Bookmark,
  Share2
} from 'lucide-react';
import { jobsAPI } from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';

const JobSearch = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    jobType: '',
    location: '',
    category: '',
    page: 1,
    limit: 10
  });
  const [totalJobs, setTotalJobs] = useState(0);
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    fetchJobs();
  }, [filters]);

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const response = await jobsAPI.getAll(filters);
      setJobs(response.data.jobs);
      setTotalJobs(response.data.total);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1 // Reset to first page when filters change
    }));
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchJobs();
  };

  const JobCard = ({ job }) => (
    <div className="card p-6 hover:shadow-lg transition-all duration-200">
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-start space-x-4">
          <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center flex-shrink-0">
            <Building2 className="w-6 h-6 text-primary-600" />
          </div>
          <div className="flex-1">
            <h3 className="text-lg font-semibold text-gray-900 mb-1">{job.title}</h3>
            <p className="text-gray-600 mb-2">{job.company?.name}</p>
            <div className="flex flex-wrap gap-2 mb-3">
              <span className="badge badge-primary">{job.jobType}</span>
              <span className="badge badge-secondary">{job.location}</span>
              <span className="badge badge-success">{job.duration}</span>
            </div>
          </div>
        </div>
        <div className="flex space-x-2">
          <button className="p-2 text-gray-400 hover:text-primary-600 transition-colors">
            <Bookmark className="w-4 h-4" />
          </button>
          <button className="p-2 text-gray-400 hover:text-primary-600 transition-colors">
            <Share2 className="w-4 h-4" />
          </button>
        </div>
      </div>

      <p className="text-gray-600 text-sm mb-4 line-clamp-2">
        {job.shortDescription || job.description.substring(0, 150)}...
      </p>

      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4 text-sm text-gray-500">
          <div className="flex items-center">
            <Calendar className="w-4 h-4 mr-1" />
            Start: {new Date(job.startDate).toLocaleDateString()}
          </div>
          <div className="flex items-center">
            <Clock className="w-4 h-4 mr-1" />
            Apply by: {new Date(job.applicationDeadline).toLocaleDateString()}
          </div>
        </div>
        
        <div className="flex space-x-2">
          <button className="btn btn-outline btn-sm">
            View Details
          </button>
          <button className="btn btn-primary btn-sm">
            Apply Now
          </button>
        </div>
      </div>
    </div>
  );

  const FilterSection = () => (
    <div className="card p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
        <Filter className="w-5 h-5 mr-2 text-primary-600" />
        Filters
      </h3>

      <div className="space-y-4">
        {/* Job Type Filter */}
        <div>
          <label className="form-label">Job Type</label>
          <select
            value={filters.jobType}
            onChange={(e) => handleFilterChange('jobType', e.target.value)}
            className="input"
          >
            <option value="">All Types</option>
            <option value="ojt">OJT</option>
            <option value="part-time">Part-time</option>
            <option value="full-time">Full-time</option>
            <option value="contract">Contract</option>
          </select>
        </div>

        {/* Location Filter */}
        <div>
          <label className="form-label">Location</label>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <MapPin className="h-4 w-4 text-gray-400" />
            </div>
            <input
              type="text"
              placeholder="Enter location"
              value={filters.location}
              onChange={(e) => handleFilterChange('location', e.target.value)}
              className="input pl-10"
            />
          </div>
        </div>

        {/* Category Filter */}
        <div>
          <label className="form-label">Category</label>
          <input
            type="text"
            placeholder="Enter category"
            value={filters.category}
            onChange={(e) => handleFilterChange('category', e.target.value)}
            className="input"
          />
        </div>

        {/* Reset Filters */}
        <button
          onClick={() => setFilters({
            search: '',
            jobType: '',
            location: '',
            category: '',
            page: 1,
            limit: 10
          })}
          className="w-full btn btn-outline"
        >
          Reset Filters
        </button>
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-heading font-bold text-gray-900 mb-2">
          Find Your Dream Opportunity
        </h1>
        <p className="text-gray-600">
          Discover OJT, internship, and job opportunities in the aerospace industry
        </p>
      </div>

      {/* Search Bar */}
      <div className="card p-6 mb-8">
        <form onSubmit={handleSearch} className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1 relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="h-5 w-5 text-gray-400" />
            </div>
            <input
              type="text"
              placeholder="Search jobs by title, company, or keywords..."
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              className="input pl-10"
            />
          </div>
          <button
            type="button"
            onClick={() => setShowFilters(!showFilters)}
            className="btn btn-outline sm:w-auto"
          >
            <Filter className="w-4 h-4 mr-2" />
            Filters
          </button>
          <button
            type="submit"
            className="btn btn-primary sm:w-auto"
          >
            <Search className="w-4 h-4 mr-2" />
            Search
          </button>
        </form>

        {/* Mobile Filters */}
        {showFilters && (
          <div className="mt-4 sm:hidden">
            <FilterSection />
          </div>
        )}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
        {/* Desktop Filters */}
        <div className="hidden lg:block">
          <FilterSection />
        </div>

        {/* Job Listings */}
        <div className="lg:col-span-3">
          {/* Results Count */}
          <div className="flex items-center justify-between mb-6">
            <p className="text-gray-600">
              {totalJobs} job{totalJobs !== 1 ? 's' : ''} found
            </p>
            <select
              value={filters.limit}
              onChange={(e) => handleFilterChange('limit', parseInt(e.target.value))}
              className="input w-auto"
            >
              <option value={10}>10 per page</option>
              <option value={25}>25 per page</option>
              <option value={50}>50 per page</option>
            </select>
          </div>

          {/* Loading State */}
          {loading && <LoadingSpinner text="Loading jobs..." />}

          {/* No Results */}
          {!loading && jobs.length === 0 && (
            <div className="text-center py-12">
              <Briefcase className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No jobs found</h3>
              <p className="text-gray-600">Try adjusting your search criteria</p>
            </div>
          )}

          {/* Job List */}
          {!loading && jobs.length > 0 && (
            <div className="space-y-4">
              {jobs.map((job) => (
                <JobCard key={job._id} job={job} />
              ))}
            </div>
          )}

          {/* Pagination */}
          {!loading && jobs.length > 0 && (
            <div className="flex justify-center mt-8">
              <div className="flex space-x-2">
                <button
                  onClick={() => handleFilterChange('page', filters.page - 1)}
                  disabled={filters.page === 1}
                  className="btn btn-outline btn-sm"
                >
                  Previous
                </button>
                <span className="flex items-center px-4 text-gray-600">
                  Page {filters.page}
                </span>
                <button
                  onClick={() => handleFilterChange('page', filters.page + 1)}
                  disabled={jobs.length < filters.limit}
                  className="btn btn-outline btn-sm"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default JobSearch;
