// src/pages/Jobs.js
import React, { useEffect, useMemo, useState } from "react";
import { jobsAPI } from "../utils/api";
import { Search, Briefcase, MapPin, Building2, ChevronDown } from "lucide-react";
import LoadingSpinner from "../components/LoadingSpinner";
import toast from "react-hot-toast";

/** Helper: simple "time ago" */
const timeAgo = (isoOrDate) => {
  if (!isoOrDate) return "";
  const d = new Date(isoOrDate);
  const diff = Math.max(0, Date.now() - d.getTime());
  const mins = Math.floor(diff / 60000);
  if (mins < 1) return "just now";
  if (mins < 60) return `${mins} min${mins > 1 ? "s" : ""} ago`;
  const hours = Math.floor(mins / 60);
  if (hours < 24) return `${hours} hour${hours > 1 ? "s" : ""} ago`;
  const days = Math.floor(hours / 24);
  if (days < 7) return `${days} day${days > 1 ? "s" : ""} ago`;
  const weeks = Math.floor(days / 7);
  if (weeks < 5) return `${weeks} week${weeks > 1 ? "s" : ""} ago`;
  const months = Math.floor(days / 30);
  return `${months} month${months > 1 ? "s" : ""} ago`;
};

const JOB_TYPES = [
  { value: "", label: "All types" },
  { value: "internship", label: "Internship" },
  { value: "ojt", label: "OJT" },
  { value: "part-time", label: "Part-time" },
  { value: "full-time", label: "Full-time" },
  { value: "contract", label: "Contract" },
];

const Jobs = () => {
  const [loading, setLoading] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);

  // filters
  const [q, setQ] = useState("");
  const [type, setType] = useState("");
  const [category, setCategory] = useState("");
  const [location, setLocation] = useState("");

  // debounce search query
  const [qInput, setQInput] = useState("");
  useEffect(() => {
    const t = setTimeout(() => setQ(qInput.trim()), 350);
    return () => clearTimeout(t);
  }, [qInput]);

  const load = async (params = {}) => {
    try {
      setLoading(true);
      const data = await jobsAPI.list({
        approvedOnly: true,
        status: "active",
        ...(params || {}),
      });
      setJobs(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
      toast.error("Failed to load jobs");
    } finally {
      setLoading(false);
    }
  };

  // initial fetch (active + approved) + categories
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const [jobsRes, catsRes] = await Promise.all([
          jobsAPI.list({ approvedOnly: true, status: "active" }),
          jobsAPI.listCategories().catch(() => []),
        ]);
        setJobs(Array.isArray(jobsRes) ? jobsRes : []);
        setCategories(Array.isArray(catsRes) ? catsRes : []);
      } catch (err) {
        console.error(err);
        toast.error("Failed to load jobs");
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // fetch on filter change
  useEffect(() => {
    const params = {
      approvedOnly: true,
      status: "active",
    };
    if (q) params.q = q;
    if (type) params.jobType = type;
    if (category) params.category = category;
    if (location) params.location = location;
    load(params);
  }, [q, type, category, location]);

  const hasFilters = useMemo(
    () => !!(q || type || category || location),
    [q, type, category, location]
  );

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-6">
        <h1 className="text-3xl font-bold tracking-tight">Current roles available</h1>
        <p className="text-gray-600 mt-1">
          Browse active and approved opportunities for PhilSCA Students & Alumni.
        </p>
      </div>

      {/* Filters bar */}
      <div className="bg-white border rounded-xl shadow-sm p-3 sm:p-4 mb-6">
        <div className="grid grid-cols-1 sm:grid-cols-5 gap-3 items-center">
          {/* Search */}
          <div className="sm:col-span-2">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                value={qInput}
                onChange={(e) => setQInput(e.target.value)}
                placeholder="Search job titles, skills..."
                className="w-full pl-9 pr-3 py-2.5 rounded-lg border focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
            </div>
          </div>

          {/* Type (smaller) */}
          <div>
            <div className="relative">
              <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400 pointer-events-none" />
              <select
                value={type}
                onChange={(e) => setType(e.target.value)}
                className="w-full appearance-none pr-8 py-2.5 rounded-lg border focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                {JOB_TYPES.map((t) => (
                  <option key={t.value} value={t.value}>
                    {t.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Category (dropdown; smaller) */}
          <div>
            <div className="relative">
              <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400 pointer-events-none" />
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="w-full appearance-none pr-8 py-2.5 rounded-lg border focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                <option value="">All categories</option>
                {categories.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Location (smaller) */}
          <div>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Location"
              className="w-full py-2.5 rounded-lg border px-3 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
          </div>
        </div>

        {/* Clear filters */}
        {hasFilters && (
          <div className="mt-3">
            <button
              onClick={() => {
                setQInput("");
                setType("");
                setCategory("");
                setLocation("");
              }}
              className="text-sm text-gray-600 hover:text-gray-900 underline"
            >
              Clear filters
            </button>
          </div>
        )}
      </div>

      {/* Results */}
      {loading ? (
        <LoadingSpinner />
      ) : jobs.length === 0 ? (
        <div className="text-gray-600">No matching active jobs.</div>
      ) : (
        <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
          {jobs.map((job) => (
            <article
              key={job._id}
              className="bg-white border rounded-xl shadow-sm p-4 hover:shadow-md transition"
            >
              <header className="mb-3">
                <h2 className="text-lg font-semibold leading-snug">{job.title}</h2>
                <p className="text-xs text-gray-500">{timeAgo(job.createdAt)}</p>
              </header>

              <p className="text-sm text-gray-700 mb-3">
                {job.shortDescription
                  ? job.shortDescription
                  : job.description
                  ? job.description.slice(0, 120) + (job.description.length > 120 ? "…" : "")
                  : ""}
              </p>

              <ul className="text-sm text-gray-600 space-y-1 mb-4">
                <li className="flex items-center gap-2">
                  <Building2 className="h-4 w-4" />
                  <span>{job.company?.name || "Company not specified"}</span>
                </li>
                <li className="flex items-center gap-2">
                  <MapPin className="h-4 w-4" />
                  <span>{job.location || "Location not specified"}</span>
                </li>
                <li className="flex items-center gap-2">
                  <Briefcase className="h-4 w-4" />
                  <span className="capitalize">{job.jobType || "—"}</span>
                </li>
              </ul>

              <div className="flex items-center gap-3">
                <a
                  href={job.applyUrl || job.applicationLink || `/jobs/${job._id}`}
                  className="inline-flex items-center justify-center px-3 py-2 text-sm font-medium rounded-md bg-indigo-600 text-white hover:bg-indigo-700"
                >
                  Apply now
                </a>

                {/* Optional LinkedIn mini-button if you store it */}
                {job.linkedInUrl && (
                  <a
                    href={job.linkedInUrl}
                    aria-label="Open on LinkedIn"
                    className="inline-flex items-center justify-center w-9 h-9 rounded-md border hover:bg-gray-50"
                  >
                    in
                  </a>
                )}
              </div>
            </article>
          ))}
        </div>
      )}
    </div>
  );
};

export default JobSearch;
