// Dashboard.js
import React, { useState, useEffect } from 'react';
import { ClipboardList } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Briefcase,
  Users,
  Building2,
  BarChart3,
  TrendingUp,
  Eye,
  FileText,
  UserCheck,
  Paintbrush
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { jobsAPI, companiesAPI, usersAPI, surveysAPI } from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';

// Chart.js
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const THEME_COLORS = {
  default: { primary: 'rgba(37, 99, 235, 0.72)', secondary: 'rgba(96, 165, 250, 0.62)' },
  emerald: { primary: 'rgba(5, 150, 105, 0.72)', secondary: 'rgba(52, 211, 153, 0.62)' },
  rose: { primary: 'rgba(225, 29, 72, 0.72)', secondary: 'rgba(251, 113, 133, 0.62)' },
  philsca: { primary: 'rgba(217, 119, 6, 0.72)', secondary: 'rgba(251, 191, 36, 0.62)' },
};

const SURVEY_CHECK_MINUTES = 30; // throttle background checks

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalJobs: 0,
    activeJobs: 0,
    totalCompanies: 0,
    featuredJobs: [],
    recentActivity: [],
    adminStats: {
      pendingApproval: 0,
      totalUsers: 0,
      newUsers: 0,
      roleCounts: { students: 0, alumni: 0, admins: 0 },
      topSearches: []
    }
  });
  const [loading, setLoading] = useState(true);
  const { user, isAdmin, isStudentOrAlumni } = useAuth();
  const navigate = useNavigate();

  /* -------------------- SURVEY TRIGGER (students/alumni) -------------------- */
  useEffect(() => {
    if (isAdmin()) return;                    // admins never get survey prompts
    if (!isStudentOrAlumni()) return;         // only for students/alumni

    const check = async (force = false) => {
      try {
        const now = Date.now();
        const last = Number(localStorage.getItem('survey_last_check') || 0);
        if (!force && now - last < SURVEY_CHECK_MINUTES * 60_000) return;

        const list = await surveysAPI.eligible(); // GET /surveys/active/eligible
        localStorage.setItem('survey_last_check', String(Date.now()));

        if (!Array.isArray(list) || list.length === 0) return;

        const prompted = JSON.parse(localStorage.getItem('survey_prompted_ids') || '[]');
        const first = list.find(s => !prompted.includes(s._id));
        if (!first) return;

        // remember we prompted (prevents bounce loop)
        localStorage.setItem('survey_prompted_ids', JSON.stringify([...prompted, first._id]));

        // send them to the answer page
        navigate(`/surveys/${first._id}/answer`, { state: { survey: first } });
      } catch (e) {
        // don't block dashboard if survey check fails
        console.warn('Survey check failed:', e?.message || e);
      }
    };

    // initial check
    check(false);

    // re-check on window focus so it feels fresh when they come back
    const onFocus = () => check(true);
    window.addEventListener('focus', onFocus);
    return () => window.removeEventListener('focus', onFocus);
  }, [isAdmin, isStudentOrAlumni, navigate]);

  /* -------------------- Admin theme (scoped) -------------------- */
  const THEMES = [
    { id: 'default', name: 'Default (Blue)', className: 'theme-default' },
    { id: 'emerald', name: 'Emerald', className: 'theme-emerald' },
    { id: 'rose', name: 'Rose', className: 'theme-rose' },
    { id: 'philsca', name: 'PhilSCA (Gold)', className: 'theme-philsca' },
  ];
  const [adminTheme, setAdminTheme] = useState(
    () => (typeof window !== 'undefined' && localStorage.getItem('admin_theme')) || 'default'
  );
  const themeClass = THEMES.find(t => t.id === adminTheme)?.className || 'theme-default';
  const themeColors = THEME_COLORS[adminTheme] || THEME_COLORS.default;

  useEffect(() => {
    if (isAdmin()) localStorage.setItem('admin_theme', adminTheme);
  }, [adminTheme, isAdmin]);

  const AdminWrapper = ({ children }) => (
    <div className={`admin-portal ${themeClass}`}>
      <style>{`
  .admin-portal.theme-default { --primary-100: 219 234 254; --primary-400: 96 165 250; --primary-600: 37 99 235; --primary-700: 29 78 216; }
  .admin-portal.theme-emerald { --primary-100: 209 250 229; --primary-400: 52 211 153; --primary-600: 5 150 105; --primary-700: 4 120 87; }
  .admin-portal.theme-rose    { --primary-100: 255 228 230; --primary-400: 251 113 133; --primary-600: 225 29 72;  --primary-700: 190 18 60; }
  .admin-portal.theme-philsca { --primary-100: 254 243 199; --primary-400: 251 191 36;  --primary-600: 217 119 6;  --primary-700: 180 83 9; }

  .admin-portal .text-primary-600 { color: rgb(var(--primary-600)) !important; }
  .admin-portal .bg-primary-100 { background-color: rgb(var(--primary-100)) !important; }
  .admin-portal .fill-primary-400 { fill: rgb(var(--primary-400)) !important; }

  .admin-portal .btn-primary { background-color: rgb(var(--primary-600)) !important; }
  .admin-portal .btn-primary:hover { background-color: rgb(var(--primary-700)) !important; }
`}</style>
      {children}
    </div>
  );

  /* -------------------- Fetch dashboard data -------------------- */
  useEffect(() => { fetchDashboardData(); }, []);

  const toInt = (v) => Number.isFinite(v) ? v : parseInt(v ?? 0, 10) || 0;

  function normalizeRoleCounts(data) {
    if (!data) return { students: 0, alumni: 0, admins: 0 };
    const c = data.byRole || data.roleCounts || data.counts;
    if (c) {
      return {
        students: toInt(c.students ?? c.student ?? 0),
        alumni:   toInt(c.alumni   ?? c.alumnus ?? 0),
        admins:   toInt(c.admins   ?? c.admin   ?? 0),
      };
    }
    const s2  = toInt(data.totalStudents ?? data.studentsCount);
    const a2  = toInt(data.totalAlumni   ?? data.alumniCount);
    const ad2 = toInt(data.totalAdmins   ?? data.adminsCount);
    if (s2 || a2 || ad2) return { students: s2, alumni: a2, admins: ad2 };
    return { students: 0, alumni: 0, admins: 0 };
  }

  function normalizeTopSearches(data) {
    const arr =
      data?.topSearches ||
      data?.searchStats ||
      data?.popularSearches ||
      data?.analytics?.topSearches ||
      [];
    return Array.isArray(arr)
      ? arr
          .map(x => ({ term: x.term ?? x.query ?? x.keyword ?? '', total: toInt(x.total ?? x.count ?? x.hits ?? 0) }))
          .filter(x => x.term)
      : [];
  }

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      if (isAdmin()) {
        const [jobsStats, companiesStats, usersStats] = await Promise.all([
          jobsAPI.getStatistics(),
          companiesAPI.getStatistics(),
          usersAPI.getStatistics()
        ]);
        const unwrap = (res) => (res?.data?.data ?? res?.data ?? res ?? {});
        const jd = unwrap(jobsStats);
        const cd = unwrap(companiesStats);
        const ud = unwrap(usersStats);

        setStats({
          totalJobs: toInt(jd.totalJobs ?? jd.total ?? 0),
          activeJobs: toInt(jd.activeJobs ?? jd.active ?? 0),
          totalCompanies: toInt(cd.totalCompanies ?? cd.total ?? 0),
          featuredJobs: [],
          recentActivity: [],
          adminStats: {
            pendingApproval: toInt(jd.pendingApproval ?? jd.pending ?? 0),
            totalUsers: toInt(ud.totalUsers ?? ud.total ?? 0),
            newUsers: toInt(ud.newUsers ?? ud.newUsers30d ?? 0),
            roleCounts: normalizeRoleCounts(ud),
            topSearches: normalizeTopSearches(jd),
          }
        });
      } else {
        const [jobsData, featuredJobsData] = await Promise.all([
          jobsAPI.getAll({ limit: 5 }),
          jobsAPI.getFeatured()
        ]);
        setStats({
          totalJobs: jobsData?.data?.total ?? 0,
          activeJobs: jobsData?.data?.jobs?.length ?? 0,
          totalCompanies: 0,
          featuredJobs: featuredJobsData?.data?.jobs || [],
          recentActivity: jobsData?.data?.jobs?.slice(0, 5) || [],
          adminStats: { pendingApproval: 0, totalUsers: 0, newUsers: 0, roleCounts: { students: 0, alumni: 0, admins: 0 }, topSearches: [] }
        });
      }
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <LoadingSpinner text="Loading dashboard..." />;

  /* -------------------- Stat card -------------------- */
  const StatCard = ({ icon: Icon, title, value }) => {
    const themed = 'bg-primary-100 text-primary-600';
    return (
      <div className="stats-card p-4 text-center">
        <div className={`w-9 h-9 ${themed} rounded-md flex items-center justify-center mb-2 mx-auto`}>
          <Icon className="w-5 h-5" />
        </div>
        <div className="text-xl font-semibold text-gray-900">{value}</div>
        <div className="text-xs text-gray-600">{title}</div>
      </div>
    );
  };

  /* -------------------- Chart.js data (theme-aware) -------------------- */
  const hasRoleData = Object.values(stats.adminStats.roleCounts || {}).some(v => v > 0);
  const hasSearchData = (stats.adminStats.topSearches || []).length > 0;

  const roleData = {
    labels: ['Students', 'Alumni', 'Admins'],
    datasets: [
      {
        label: 'Users',
        data: [
          stats.adminStats.roleCounts.students || 0,
          stats.adminStats.roleCounts.alumni || 0,
          stats.adminStats.roleCounts.admins || 0,
        ],
        backgroundColor: themeColors.primary,
        borderRadius: 6,
      },
    ],
  };

  const searchesData = {
    labels: (stats.adminStats.topSearches || []).map(s => s.term),
    datasets: [
      {
        label: 'Searches',
        data: (stats.adminStats.topSearches || []).map(s => s.total),
        backgroundColor: themeColors.secondary,
        borderRadius: 6,
      },
    ],
  };

  const baseOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: { legend: { display: false }, tooltip: { titleFont: { size: 12 }, bodyFont: { size: 11 } } },
    scales: {
      x: { ticks: { font: { size: 11 }, color: '#4B5563' }, grid: { display: false } },
      y: { beginAtZero: true, ticks: { font: { size: 11 }, color: '#4B5563', stepSize: 1 }, grid: { color: '#E5E7EB' } },
    },
    elements: { bar: { borderSkipped: false } },
  };

  const verticalOptions = baseOptions;
  const horizontalOptions = { ...baseOptions, indexAxis: 'y' };

  /* -------------------- Admin page -------------------- */
  const adminPage = (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      {/* Header */}
      <div className="mb-6 flex items-start justify-between gap-3">
        <div>
          <h1 className="text-2xl font-heading font-bold text-gray-900">
            Welcome back, {user?.firstName || 'Admin'}!
          </h1>
          <p className="text-sm text-gray-600 mt-1">
            Manage the AeroJob platform and monitor system activity.
          </p>
        </div>
        <label className="inline-flex items-center gap-2 text-xs text-gray-700">
          <Paintbrush className="w-4 h-4 text-primary-600" />
          <span>Theme</span>
          <select
            value={adminTheme}
            onChange={(e) => setAdminTheme(e.target.value)}
            className="input w-auto py-1 px-2 text-xs"
          >
            {THEMES.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </label>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-6">
        <StatCard icon={Briefcase} title="Total Jobs" value={stats.totalJobs} />
        <StatCard icon={FileText} title="Pending Approval" value={stats.adminStats?.pendingApproval || 0} color="warning" />
        <StatCard icon={Users} title="Total Users" value={stats.adminStats?.totalUsers || 0} color="success" />
        <StatCard icon={UserCheck} title="New Users (30d)" value={stats.adminStats?.newUsers || 0} color="primary" />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Users by Role */}
        <div className="card">
          <div className="px-4 py-3 border-b">
            <h3 className="text-sm font-semibold text-gray-900 flex items-center">
              <BarChart3 className="w-4 h-4 mr-2 text-primary-600" />
              Total Users by Role
            </h3>
          </div>
          <div className="p-4 h-48">
            {hasRoleData ? (
              <Bar data={roleData} options={verticalOptions} />
            ) : (
              <div className="text-sm text-gray-500 text-center py-8">No data available</div>
            )}
          </div>
          <div className="px-4 py-2 text-[11px] text-gray-500 border-t">
            Shows current totals per role.
          </div>
        </div>

        {/* Most Searched Jobs */}
        <div className="card">
          <div className="px-4 py-3 border-b">
            <h3 className="text-sm font-semibold text-gray-900 flex items-center">
              <TrendingUp className="w-4 h-4 mr-2 text-primary-600" />
              Most Searched Jobs
            </h3>
          </div>
          <div className="p-4 h-48">
            {hasSearchData ? (
              <Bar data={searchesData} options={horizontalOptions} />
            ) : (
              <div className="text-sm text-gray-500 text-center py-8">No data available</div>
            )}
          </div>
          <div className="px-4 py-2 text-[11px] text-gray-500 border-t">
            Aggregated searches by users & alumni.
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="mt-6">
        <h3 className="text-sm font-semibold text-gray-900 mb-3">Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
          <Link to="/admin/jobs" className="card p-5 text-center hover:shadow-sm transition-shadow">
            <Briefcase className="w-7 h-7 text-primary-600 mx-auto mb-2" />
            <h4 className="font-semibold text-gray-900 text-sm mb-1">Manage Jobs</h4>
            <p className="text-gray-600 text-xs">Approve and manage job postings</p>
          </Link>
          <Link to="/admin/companies" className="card p-5 text-center hover:shadow-sm transition-shadow">
            <Building2 className="w-7 h-7 text-primary-600 mx-auto mb-2" />
            <h4 className="font-semibold text-gray-900 text-sm mb-1">Manage Companies</h4>
            <p className="text-gray-600 text-xs">View and manage company profiles</p>
          </Link>
          <Link to="/admin/users" className="card p-5 text-center hover:shadow-sm transition-shadow">
            <Users className="w-7 h-7 text-primary-600 mx-auto mb-2" />
            <h4 className="font-semibold text-gray-900 text-sm mb-1">View Users</h4>
            <p className="text-gray-600 text-xs">Monitor user activity and statistics</p>
          </Link>
          <Link to="/admin/surveys" className="card hover:shadow-md transition">
            <div className="card-body flex items-center gap-3">
              <div className="p-3 rounded-xl bg-gray-100">
                <ClipboardList className="w-6 h-6" />
              </div>
              <div>
                <div className="font-semibold">Survey</div>
                <div className="text-xs opacity-70">Create questions & view responses</div>
              </div>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );

  /* -------------------- Non-admin page -------------------- */
  const userPage = (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      <div className="mb-6">
        <h1 className="text-2xl font-heading font-bold text-gray-900">
          Welcome back, {user?.firstName}!
        </h1>
        <p className="text-sm text-gray-600 mt-1">
          Discover new opportunities and track your job search progress.
        </p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-6">
        <StatCard icon={Briefcase} title="Available Jobs" value={stats.totalJobs} />
        <StatCard icon={Eye} title="Jobs Viewed" value={12} color="success" />
        <StatCard icon={FileText} title="Applications" value={3} color="warning" />
        <StatCard icon={Building2} title="Companies" value={stats.totalCompanies} color="primary" />
      </div>
    </div>
  );

  return isAdmin() ? <AdminWrapper>{adminPage}</AdminWrapper> : userPage;
};

export default Dashboard;
