// src/pages/JobSearch.js
import React, { useEffect, useMemo, useState } from 'react';
import { Plus, X, Search } from 'lucide-react';
import { jobsAPI, companiesAPI } from '../utils/api';
import toast from 'react-hot-toast';

/**
 * Lightweight way to know the role without wiring your full auth context.
 * If you already have an auth hook, replace this with it.
 * Expecting localStorage 'aj-user' to optionally be: { role: 'admin' | 'student' | 'alumni', ... }
 */
function useAuthLite() {
  const [user] = useState(() => {
    try {
      const raw = localStorage.getItem('aj-user');
      return raw ? JSON.parse(raw) : { role: 'student' };
    } catch {
      return { role: 'student' };
    }
  });
  return { user, isAdmin: user?.role === 'admin' };
}

const defaultForm = {
  title: '',
  company: '',
  location: '',
  jobType: 'Internship',
  category: '',
  description: '',
  shortDescription: '',
  requirements: '',
  applicationDeadline: '',
  isRemote: false,
  isHybrid: false,
};

const JobSearch = () => {
  const { isAdmin } = useAuthLite();

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [companies, setCompanies] = useState([]);

  const [q, setQ] = useState('');
  const [type, setType] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');

  // Admin create modal state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form, setForm] = useState(defaultForm);

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const [jobsRaw, catsRaw, compsRaw] = await Promise.all([
          jobsAPI.list({ approvedOnly: true, status: 'active' }),
          // If your api.js doesn't have listCategories, the catch keeps UI working
          jobsAPI.listCategories?.().catch(() => []),
          companiesAPI.list().catch(() => []),
        ]);

        setJobs(Array.isArray(jobsRaw) ? jobsRaw : jobsRaw?.jobs || []);
        setCategories(Array.isArray(catsRaw) ? catsRaw : catsRaw?.categories || []);
        setCompanies(Array.isArray(compsRaw) ? compsRaw : compsRaw?.companies || []);
      } catch (e) {
        console.error('JobSearch load error:', e);
        toast.error(e.message || 'Failed to load jobs');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const filtered = useMemo(() => {
    const _q = q.trim().toLowerCase();
    return jobs.filter((j) => {
      const byQ =
        !_q ||
        j.title?.toLowerCase().includes(_q) ||
        j.description?.toLowerCase().includes(_q) ||
        j.company?.name?.toLowerCase().includes(_q);
      const byType = !type || j.jobType === type;
      const byCat = !category || (Array.isArray(j.categories) && j.categories.includes(category));
      const byLoc = !location || j.location?.toLowerCase().includes(location.toLowerCase());
      return byQ && byType && byCat && byLoc;
    });
  }, [jobs, q, type, category, location]);

  const applyFilters = async () => {
    try {
      setLoading(true);
      const params = {
        approvedOnly: true,
        status: 'active',
        q: q || undefined,
        jobType: type || undefined,
        category: category || undefined,
        location: location || undefined,
      };
      const raw = await jobsAPI.list(params);
      setJobs(Array.isArray(raw) ? raw : raw?.jobs || []);
    } catch (e) {
      console.error('Apply filter error:', e);
      toast.error(e.message || 'Failed to load jobs');
    } finally {
      setLoading(false);
    }
  };

  const clearFilters = () => {
    setQ('');
    setType('');
    setCategory('');
    setLocation('');
    applyFilters();
  };

  /* ---------------------- Admin-only create flow ---------------------- */
  const openModal = () => {
    setForm(defaultForm);
    setIsModalOpen(true);
  };
  const closeModal = () => setIsModalOpen(false);

  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((p) => ({ ...p, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    if (!form.title || !form.company || !form.location || !form.jobType || !form.description) {
      toast.error('Please fill the required fields.');
      return;
    }
    try {
      setSaving(true);
      const requirementsArr = form.requirements
        ? form.requirements.split('\n').map((s) => s.trim()).filter(Boolean)
        : [];

      const payload = {
        title: form.title.trim(),
        company: form.company, // ObjectId
        location: form.location.trim(),
        jobType: form.jobType,
        description: form.description.trim(),
        shortDescription: form.shortDescription?.trim() || undefined,
        requirements: requirementsArr,
        applicationDeadline: form.applicationDeadline || undefined,
        isRemote: !!form.isRemote,
        isHybrid: !!form.isHybrid,
        categories: form.category ? [form.category] : [],
        // Optional (server already sets these):
        status: 'active',
        isApproved: true,
      };

      const created = await jobsAPI.create(payload);
      setJobs((prev) => [created, ...prev]);

      if (form.category && !categories.includes(form.category)) {
        setCategories((prev) => [...prev, form.category]);
      }

      toast.success('Job created');
      setIsModalOpen(false);
    } catch (e) {
      console.error('Create job error:', e);
      toast.error(e.message || 'Failed to add job');
    } finally {
      setSaving(false);
    }
  };

  /* ------------------------------- UI -------------------------------- */
  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-2xl font-semibold">Find Jobs</h1>

        {/* Admin-only create button */}
        {isAdmin && (
          <button
            onClick={openModal}
            className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-600 text-white"
          >
            <Plus size={18} /> Create Job
          </button>
        )}
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-3 mb-4">
        <div className="relative flex-1 min-w-[220px]">
          <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            className="w-full pl-9 pr-3 py-2 rounded-lg border border-gray-200"
            placeholder="Search job title or keywords"
          />
        </div>

        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="px-3 py-2 rounded-lg border border-gray-200"
        >
          <option value="">Type</option>
          {['Internship', 'OJT', 'Part-time', 'Full-time', 'Contract'].map((t) => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>

        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="px-3 py-2 rounded-lg border border-gray-200"
        >
          <option value="">Category</option>
          {categories.map((c) => (
            <option key={c} value={c}>{c}</option>
          ))}
        </select>

        <input
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="px-3 py-2 rounded-lg border border-gray-200"
          placeholder="Location"
        />

        <button onClick={applyFilters} className="px-4 py-2 rounded-lg bg-blue-600 text-white">
          Apply Filters
        </button>
        <button onClick={clearFilters} className="px-4 py-2 rounded-lg border">
          Clear
        </button>
      </div>

      {/* Results */}
      {loading ? (
        <div className="text-gray-500">Loading…</div>
      ) : filtered.length === 0 ? (
        <div className="text-gray-500">No jobs found.</div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2">
          {filtered.map((j) => (
            <div key={j._id} className="rounded-xl border p-4">
              <div className="font-semibold">{j.title}</div>
              <div className="text-sm text-gray-600">
                {j.company?.name || '—'} • {j.location || '—'}
              </div>
              <div className="text-sm mt-1">
                {j.jobType} {j.categories?.[0] ? `• ${j.categories[0]}` : ''}
              </div>
              {(j.shortDescription || j.description) && (
                <p className="text-sm mt-2 text-gray-700 line-clamp-3">
                  {j.shortDescription || j.description}
                </p>
              )}
              {j.applicationDeadline && (
                <div className="text-xs text-gray-500 mt-2">
                  Deadline: {new Date(j.applicationDeadline).toLocaleDateString()}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Admin-only Add Job Modal */}
      {isAdmin && isModalOpen && (
        <div className="fixed inset-0 z-40 bg-black/40 flex items-center justify-center p-4">
          <div className="bg-white rounded-2xl w-full max-w-xl p-6 shadow-xl">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold">Add New Job</h2>
              <button onClick={closeModal} className="p-1 rounded hover:bg-gray-100">
                <X size={18} />
              </button>
            </div>

            <form onSubmit={handleCreate} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Job Title *</label>
                <input
                  name="title"
                  value={form.title}
                  onChange={handleFormChange}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                  placeholder="e.g., Quality Assurance Engineer"
                />
              </div>

              <div>
                <label className="text-sm font-medium">Company *</label>
                <select
                  name="company"
                  value={form.company}
                  onChange={handleFormChange}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                >
                  <option value="">Select company</option>
                  {companies.map((c) => (
                    <option key={c._id} value={c._id}>{c.name}</option>
                  ))}
                </select>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                <div>
                  <label className="text-sm font-medium">Job Type *</label>
                  <select
                    name="jobType"
                    value={form.jobType}
                    onChange={handleFormChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                  >
                    {['Internship', 'OJT', 'Part-time', 'Full-time', 'Contract'].map((t) => (
                      <option key={t} value={t}>{t}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="text-sm font-medium">Category</label>
                  <select
                    name="category"
                    value={form.category}
                    onChange={handleFormChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                  >
                    <option value="">None</option>
                    {categories.map((cat) => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="text-sm font-medium">Location *</label>
                  <input
                    name="location"
                    value={form.location}
                    onChange={handleFormChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                    placeholder="e.g., Quezon City"
                  />
                </div>
              </div>

              <div>
                <label className="text-sm font-medium">Short Description</label>
                <input
                  name="shortDescription"
                  value={form.shortDescription}
                  onChange={handleFormChange}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                />
              </div>

              <div>
                <label className="text-sm font-medium">Description *</label>
                <textarea
                  name="description"
                  value={form.description}
                  onChange={handleFormChange}
                  rows={4}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                />
              </div>

              <div>
                <label className="text-sm font-medium">Requirements (one per line)</label>
                <textarea
                  name="requirements"
                  value={form.requirements}
                  onChange={handleFormChange}
                  rows={3}
                  className="mt-1 w-full border rounded-lg px-3 py-2"
                />
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                <div>
                  <label className="text-sm font-medium">Application Deadline</label>
                  <input
                    type="date"
                    name="applicationDeadline"
                    value={form.applicationDeadline}
                    onChange={handleFormChange}
                    className="mt-1 w-full border rounded-lg px-3 py-2"
                  />
                </div>
                <label className="flex items-center gap-2 mt-6">
                  <input
                    type="checkbox"
                    name="isRemote"
                    checked={form.isRemote}
                    onChange={handleFormChange}
                  />
                  <span className="text-sm">Remote</span>
                </label>
                <label className="flex items-center gap-2 mt-6">
                  <input
                    type="checkbox"
                    name="isHybrid"
                    checked={form.isHybrid}
                    onChange={handleFormChange}
                  />
                  <span className="text-sm">Hybrid</span>
                </label>
              </div>

              <div className="flex items-center justify-end gap-2 pt-2">
                <button type="button" onClick={closeModal} className="px-4 py-2 rounded-lg border">
                  Cancel
                </button>
                <button
                  disabled={saving}
                  className="px-4 py-2 rounded-lg bg-blue-600 text-white disabled:opacity-60"
                >
                  {saving ? 'Saving…' : 'Add Job'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default JobSearch;
