import React, { useEffect, useState } from 'react';
import { surveysAPI } from '../../utils/api';
import { Link } from 'react-router-dom';
import { ClipboardList, Plus, Edit3, Trash2, Eye } from 'lucide-react';
import toast from 'react-hot-toast';
import LoadingSpinner from '../../components/LoadingSpinner';

export default function SurveyList() {
  const [loading, setLoading] = useState(true);
  const [surveys, setSurveys] = useState([]);

  const load = async () => {
    setLoading(true);
    try {
      const data = await surveysAPI.list();
      setSurveys(data);
    } catch (e) {
      toast.error('Failed to load surveys');
    } finally { setLoading(false); }
  };

  useEffect(() => { load(); }, []);

  const del = async (id) => {
    if (!confirm('Delete this survey?')) return;
    await surveysAPI.remove(id);
    toast.success('Survey deleted');
    load();
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="max-w-5xl mx-auto p-4">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold flex items-center gap-2"><ClipboardList size={24}/> Surveys</h1>
        <Link className="btn btn-primary flex items-center gap-2" to="/admin/surveys/new"><Plus size={18}/> New Survey</Link>
      </div>

      <div className="grid gap-4">
        {surveys.map(s => (
          <div className="card" key={s._id}>
            <div className="card-body flex items-center justify-between">
              <div>
                <div className="font-semibold">{s.title} {s.isActive ? <span className="badge badge-success ml-2">Active</span> : <span className="badge">Inactive</span>}</div>
                <div className="text-sm opacity-70">{s.description}</div>
                <div className="text-xs mt-1">Audience: {s.audience.toUpperCase()} • {s.questions?.length || 0} questions</div>
              </div>
              <div className="flex gap-2">
                <Link className="btn btn-outline flex items-center gap-1" to={`/admin/surveys/${s._id}/responses`}><Eye size={16}/> Responses</Link>
                <Link className="btn btn-outline flex items-center gap-1" to={`/admin/surveys/${s._id}/edit`}><Edit3 size={16}/> Edit</Link>
                <button className="btn btn-danger flex items-center gap-1" onClick={() => del(s._id)}><Trash2 size={16}/> Delete</button>
              </div>
            </div>
          </div>
        ))}
        {!surveys.length && <div className="text-center opacity-70">No surveys yet.</div>}
      </div>
    </div>
  );
}
